export function EditComponent(Map, component, userInfo) {
  return function (dispatch) {
    dispatch(initRequest(true));
    let urlArray = [];
    const ComponentTestBySpec = rsapi.post("/registration/getComponentBySpec", Map);
    const timezone = rsapi.post("timezone/getTimeZone");
    urlArray = [ComponentTestBySpec, timezone];
    Axios.all(urlArray).then(response => {
      const lstComponentMap = constructOptionList(response[0].data.lstComponent || [], "ncomponentcode", "scomponentname", undefined, undefined, true);
      const timeZoneListMap = constructOptionList(response[1].data || [], "ntimezonecode", "stimezoneid", undefined, undefined, true);
      const TimeZoneField = response[1].data;
      const lstComponent = lstComponentMap.get("OptionList");
      const timeZoneList = timeZoneListMap.get("OptionList");
      component["ncomponentcode"] = {
        label: component.scomponentname,
        value: component.ncomponentcode
      };
      component["dreceiveddate"] = rearrangeDateFormat(userInfo, component["sreceiveddate"]);
      component["scomments"] = component["scomments"];
      let ntzdreceivedate = component.ntzdreceivedate;

      if (component.ntzdreceivedate !== null && typeof component.ntzdreceivedate === "object") {
        ntzdreceivedate = component.ntzdreceivedate.value;
      }

      component["ntzdreceivedate"] = component.ntzdreceivedate ? {
        "label": TimeZoneField[TimeZoneField.findIndex(x => x.ntimezonecode === ntzdreceivedate)].stimezoneid,
        "value": TimeZoneField[TimeZoneField.findIndex(x => x.ntimezonecode === ntzdreceivedate)].ntimezonecode
      } : "";
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loadComponent: true,
          childoperation: "update",
          lstComponent,
          ChildscreenName: "Component",
          openChildModal: false,
          selectComponent: component,
          parentPopUpSize: "lg",
          timeZoneList,
          loading: false
        }
      });
    }).catch(error => {
      toast.error(error.message);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });
    });
  };
}
