import React from 'react';
import { Col, Row } from 'react-bootstrap';
import { injectIntl } from 'react-intl';
import DropZone from '../../components/dropzone/dropzone.component';
import { attachmentType, transactionStatus } from '../../components/Enumeration';
import { deleteAttachmentDropZone, onDropAttachFileList } from '../../components/CommonScript';
import { viewAttachment } from '../../actions';
import { connect } from 'react-redux';
import DataGrid from '../../components/data-grid/data-grid.component';
import SlideOutModal from '../../components/slide-out-modal/SlideOutModal';
import { process } from '@progress/kendo-data-query';
import CustomSwitch from '../../components/custom-switch/custom-switch.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import Esign from '../audittrail/Esign';
import rsapi from '../../rsapi';
import { toast } from 'react-toastify';
import { create_UUID, Lims_JSON_stringify, showEsign } from "../../components/CommonScript";

class ReleaseTestAttachment extends React.Component {
    constructor(props) {
        super(props)
        const dataState = {
            skip: 0,
            take: this.props.settings ? parseInt(this.props.settings[14]) : 5,
        };
        this.state = {
            loading: false,
            ReleaseTestAttachmentDetails: this.props.ReleaseTestAttachmentDetails,
            dataState: dataState,
            openModal: false,
            inputParam: this.props.inputParam,
            loadEsign: false,
            operation: "",
            selectedRecord: {},
            isneedReport: this.props.isneedReport
        }
    }

    componentDidUpdate(prevProps) {
        if (this.props.ReleaseTestAttachmentDetails !== prevProps.ReleaseTestAttachmentDetails) {
            this.setState({ ReleaseTestAttachmentDetails: this.props.ReleaseTestAttachmentDetails })
        }
        if (this.props.inputParam !== prevProps.inputParam) {
            this.setState({ inputParam: this.props.inputParam })
        }

    }

    closeModal = () => {
        let loadEsign = this.state.loadEsign;
        let openModal = this.state.openModal
        let selectedRecord = this.state.selectedRecord;
        let operation = this.state.operation;
        if (this.state.loadEsign) {
            if (this.state.operation === "delete") {
                loadEsign = false;
                openModal = false;
                selectedRecord = {};
                operation = undefined;
            }
            else {
                loadEsign = false;
                selectedRecord["esigncomments"] = "";
                selectedRecord["esignpassword"] = "";
                selectedRecord['esignreason'] = '';
            }
        }
        else {
            openModal = false;
            selectedRecord = {};
        }
        this.setState({ loadEsign, openModal, selectedRecord, operation })
    }

    render() {
        const addReleaseTestAttachment =
            this.props.controlMap.has("AddReleaseTestAttachment") &&
            this.props.controlMap.get("AddReleaseTestAttachment").ncontrolcode;

        const editReleaseTestAttachment =
            this.props.controlMap.has("EditReleaseTestAttachment") &&
            this.props.controlMap.get("EditReleaseTestAttachment").ncontrolcode;

        const editReleaseTestAttachmentParam = {
            screenName: this.props.intl.formatMessage({ id: "IDS_RELEASETESTATTACHMENT" }),
            operation: "update", primaryKeyField: "nreleasetestattachmentcode",
            inputParam: this.state.inputParam,
            userInfo: this.props.userInfo,
            ncontrolCode: editReleaseTestAttachment
        };

        this.fieldsForReleasedTestAttachmentGrid =
            [
                { "idsName": "IDS_FILENAME", "dataField": "sfilename", "width": "200px" },
                { "idsName": "IDS_ARNO", "dataField": "sarno", "width": "200px" },
                { "idsName": "IDS_SAMPLEARNO", "dataField": "ssamplearno", "width": "200px" },
                { "idsName": "IDS_TESTSYNONYM", "dataField": "stestsynonym", "width": "200px" },
                { "idsName": "IDS_SCREENNAME", "dataField": "sformname", "width": "200px" },
                { "idsName": "IDS_DESCRIPTION", "dataField": "sdescription", "width": "200px" },
                { "idsName": "IDS_INCULDEINREPORT", "dataField": "sneedreport", "width": "200px" },
                { "idsName": "IDS_USERNAME", "dataField": "susername", "width": "200px" },
                { "idsName": "IDS_USERROLENAME", "dataField": "suserrolename", "width": "200px" },
                { "idsName": "IDS_CREATEDDATE", "dataField": "screateddate", "width": "200px" },

            ]

        let mandatoryFields = [
            { "idsName": "IDS_FILE", "dataField": "sfilename", "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" },
            { "idsName": "IDS_ARNUMBER", "dataField": "sarno", "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" },
            { "idsName": "IDS_SAMPLEARNO", "dataField": "ssamplearno", "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" },
            { "idsName": "IDS_TESTSYNONYM", "dataField": "stestsynonym", "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" }
        ];

        const sdescrption = this.state.selectedRecord && this.state.selectedRecord.nattachmenttypecode === attachmentType.LINK ? "slinkdescription" : "sdescription";
        return (
            <>
                <Row>
                    <Col>
                        <DataGrid
                            primaryKeyField="nreleasetestattachmentcode"
                            data={this.state.ReleaseTestAttachmentDetails || []}
                            dataResult={process(this.state.ReleaseTestAttachmentDetails && this.state.ReleaseTestAttachmentDetails || [], this.state.dataState)}
                            dataState={this.state.dataState}
                            isExportExcelRequired={false}
                            controlMap={this.props.controlMap}
                            userRoleControlRights={this.props.userRoleControlRights}
                            userInfo={this.props.userInfo}
                            editParam={editReleaseTestAttachmentParam}
                            extractedColumnList={this.fieldsForReleasedTestAttachmentGrid}
                            pageable={true}
                            dataStateChange={this.dataStateChange}
                            scrollable={'scrollable'}
                            gridHeight={'630px'}
                            isActionRequired={true}
                            addRecord={(event) => this.getSampleDetails(event, addReleaseTestAttachment, "create")}
                            isToolBarRequired={true}
                            methodUrl={'ReleaseTestAttachment'}
                            isAddRequired={this.props.isAddRequired}
                            isRefreshRequired={this.props.isRefreshRequired}
                            isImportRequired={this.props.isImportRequired}
                            isDownloadPDFRequired={this.props.isDownloadPDFRequired}
                            isDownloadExcelRequired={this.props.isDownloadExcelRequired}
                            fetchRecord={(event) => { this.getSampleDetails(event, editReleaseTestAttachment, "update") }}
                            deleteRecord={this.deleteRecord}
                            viewDownloadFile={this.viewReleaseTestAttachmentFile}
                        />
                    </Col>
                </Row>

                {this.state.openModal ?
                    <SlideOutModal show={this.state.openModal}
                        size={"xl"}
                        closeModal={this.closeModal}
                        operation={this.state.operation}
                        inputParam={this.state.inputParam}
                        screenName={this.props.intl.formatMessage({ id: "IDS_TESTATTACHMENTS" })}
                        onSaveClick={this.onSaveClick}
                        esign={this.state.loadEsign}
                        validateEsign={this.validateEsign}
                        selectedRecord={this.state.selectedRecord || {}}
                        mandatoryFields={mandatoryFields}
                        addComponent={this.state.loadEsign ?
                            <Esign operation={this.state.operation}
                                onInputOnChange={this.onInputOnChange}
                                inputParam={this.state.inputParam}
                                selectedRecord={this.state.selectedRecord || {}}
                            />
                            :
                            <Row>
                                {
                                    <Col md={12}>
                                        <DropZone
                                            name='AttachmentFile'
                                            label={this.props.intl.formatMessage({ id: "IDS_FILE" })}
                                            isMandatory={true}
                                            accept={this.state.selectedRecord && this.state.selectedRecord.nneedreport === transactionStatus.YES ? this.props.settings && this.props.settings[32] : ""}
                                            maxFiles={this.state.operation === "update" ? 1 : 3}
                                            minSize={0}
                                            maxSize={this.props.maxSize}
                                            onDrop={(event) => this.onDrop(event, 'sfilename', 1)}
                                            deleteAttachment={this.deleteAttachment}
                                            actionType={this.actionType}
                                            fileNameLength={this.fileNameLength}
                                            editFiles={this.state.selectedRecord ? this.state.selectedRecord : {}}
                                            attachmentTypeCode={this.state.selectedRecord && this.state.selectedRecord.selectedAttachmentFile && this.state.selectedRecord.selectedAttachmentFile.nattachmenttypecode}
                                            fileSizeName="nfilesize"
                                            fileName="sfilename"
                                        />
                                    </Col>
                                }

                                <Col md={12}>
                                    <FormSelectSearch
                                        formLabel={this.props.intl.formatMessage({ id: "IDS_ARNUMBER" })}
                                        isSearchable={true}
                                        name={"npreregno"}
                                        isDisabled={this.state.operation === "create" ? false : true}
                                        placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                        isMandatory={true}
                                        options={this.state.selectedRecord ? this.state.selectedRecord.RegistrationArno : []}
                                        optionId='npreregno'
                                        optionValue='sarno'
                                        value={this.state.selectedRecord ? this.state.selectedRecord.npreregno : ""}
                                        onChange={(event) => this.onComboChange(event, 'npreregno')}
                                        closeMenuOnSelect={true}
                                        alphabeticalSort={true}
                                    />
                                </Col>

                                <Col md={12}>
                                    <FormSelectSearch
                                        formLabel={this.props.intl.formatMessage({ id: "IDS_SAMPLEARNO" })}
                                        isSearchable={true}
                                        name={"ntransactionsamplecode"}
                                        isDisabled={this.state.operation === "create" ? false : true}
                                        placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                        isMandatory={true}
                                        options={this.state.selectedRecord ? this.state.selectedRecord.RegistrationSampleArno : []}
                                        optionId='ntransactionsamplecode'
                                        optionValue='ssamplearno'
                                        value={this.state.selectedRecord ? this.state.selectedRecord.ntransactionsamplecode : ""}
                                        onChange={(event) => this.onComboChange(event, 'ntransactionsamplecode')}
                                        closeMenuOnSelect={true}
                                        alphabeticalSort={true}
                                    />
                                </Col>

                                <Col md={12}>
                                    <FormSelectSearch
                                        formLabel={this.props.intl.formatMessage({ id: "IDS_TESTSYNONYM" })}
                                        isSearchable={true}
                                        name={"ntransactiontestcode"}
                                        isDisabled={this.state.operation === "create" ? false : true}
                                        placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                        isMandatory={true}
                                        options={this.state.selectedRecord ? this.state.selectedRecord.RegistrationTest : []}
                                        optionId='ntransactiontestcode'
                                        optionValue='stestsynonym'
                                        value={this.state.selectedRecord ? this.state.selectedRecord.ntransactiontestcode : ""}
                                        onChange={(event) => this.onComboChange(event, 'ntransactiontestcode')}
                                        closeMenuOnSelect={true}
                                        alphabeticalSort={true}
                                    />
                                </Col>

                                <Col md="12" className="mt-4">
                                    <FormTextarea
                                        name={sdescrption}
                                        label={this.props.intl.formatMessage({ id: "IDS_DESCRIPTION" })}
                                        onChange={(event) => this.onInputOnChange(event, 1)}
                                        placeholder={this.props.intl.formatMessage({ id: "IDS_DESCRIPTION" })}
                                        value={this.state.selectedRecord && this.state.selectedRecord[sdescrption] ? this.state.selectedRecord[sdescrption] : ""}
                                        rows="2"
                                        required={false}
                                        maxLength={255}
                                    >
                                    </FormTextarea>
                                </Col>

                                <Col md="12" className="mt-4">
                                    <CustomSwitch
                                        label={this.props.intl.formatMessage({ id: "IDS_INCULDEINREPORT" })}
                                        type="switch"
                                        name={"nneedreport"}
                                        onChange={(event) => this.onInputOnChange(event, 1)}
                                        defaultValue={false}
                                        isMandatory={false}
                                        required={true}
                                        checked={this.state.selectedRecord ? this.state.selectedRecord.nneedreport === transactionStatus.YES ? true : false : false}
                                    />
                                </Col>
                                {/* : ""} */}

                            </Row>
                        }
                    /> : ""}
            </>
        );
    };

    getSampleDetails = (event, ncontrolCode, operation) => {
        const url = "release/getActiveReleaseTestAttachmentById";
        let openModal = true;
        if (this.props.masterData.selectedReleaseHistory.ntransactionstatus !== transactionStatus.RELEASED) {
            if (operation === "update") {
                rsapi.post(url, {
                    "ncoaparentcode": event.editRow.ncoaparentcode,
                    "npreregno": event.editRow.npreregno,
                    "ntransactionsamplecode": event.editRow.ntransactionsamplecode,
                    "ntransactiontestcode": event.editRow.ntransactiontestcode,
                    "nreleasetestattachmentcode": event.editRow.nreleasetestattachmentcode,
                    "userinfo": this.props.userInfo
                })
                    .then(response => {
                        let responseData = response.data;
                        if (responseData.ntransactionstatus !== transactionStatus.RELEASED) {
                            let selectedReleaseTestAttachment = event.editRow;
                            let masterData = this.props.masterData;
                            let nneedreport = responseData && responseData.nneedreport ? responseData.nneedreport : transactionStatus.NO;
                            let sdescription = responseData && responseData.sdescription ? responseData.sdescription : "";
                            let sfilename = event.editRow && event.editRow.sfilename;
                            let npreregno;
                            let ntransactionsamplecode;
                            let ntransactiontestcode;
                            let sarno;
                            let ssamplearno;
                            let stestsynonym;
                            masterData.RegistrationArno.map(item => {
                                if (item.value === responseData.npreregno) {
                                    npreregno = item;
                                    sarno = item.label;
                                }
                            });
                            masterData.RegistrationSampleArno.map(item => {
                                if (item.value === responseData.ntransactionsamplecode) {
                                    ntransactionsamplecode = item;
                                    ssamplearno = item.label;
                                }
                            });
                            masterData.RegistrationTest.map(item => {
                                if (item.value === responseData.ntransactiontestcode) {
                                    ntransactiontestcode = item;
                                    stestsynonym = item.label;
                                }
                            });

                            let selectedRecord = {
                                ...this.state.selectedRecord, npreregno, ntransactionsamplecode, ntransactiontestcode, sfilename, sarno, ssamplearno, stestsynonym, nneedreport, sdescription, selectedReleaseTestAttachment
                            };
                            this.setState({
                                selectedRecord, openModal, operation, ncontrolCode
                            });
                        } else {
                            toast.warn(this.props.intl.formatMessage({ id: "IDS_RECORDALREADYRELEASED" }));
                        }
                    }).catch(error => {
                        if (error.response.status === 500) {
                            toast.error(error.message);
                        } else {
                            toast.warn(error.response.data);
                        }
                        this.setState({
                            loading: false
                        });
                    });
            } else {
                let RegistrationArno = this.props.masterData.RegistrationArno;
                let selectedRecord = {
                    RegistrationArno: RegistrationArno,
                    ReleaseTestAttachmentDetails: this.props.masterData.ReleaseTestAttachmentDetails,
                    nneedreport: transactionStatus.NO
                };
                this.setState({
                    selectedRecord, openModal, operation, ncontrolCode
                });
            }
        } else {
            toast.warn(this.props.intl.formatMessage({ id: "IDS_RECORDALREADYRELEASED" }));
        }
    }

    deleteRecord = (deleteParam) => {
        const url = "release/deleteReleaseTestAttachment";
        if (this.props.masterData.selectedReleaseHistory.ntransactionstatus !== transactionStatus.RELEASED) {
            let releasetestattachment = {
                "ncoaparentcode": deleteParam.selectedRecord.ncoaparentcode,
                "npreregno": deleteParam.selectedRecord.npreregno,
                "ntransactionsamplecode": deleteParam.selectedRecord.ntransactionsamplecode,
                "ntransactiontestcode": deleteParam.selectedRecord.ntransactiontestcode,
                "nreleasetestattachmentcode": deleteParam.selectedRecord.nreleasetestattachmentcode
            }
            rsapi.post(url, {
                releasetestattachment, "userinfo": this.props.userInfo
            })
                .then(response => {
                    let ReleaseTestAttachmentDetails = response.data.ReleaseTestAttachmentDetails;
                    let selectedRecord = {
                        ...this.state.selectedRecord, ...response.data
                    };
                    this.setState({
                        selectedRecord, ReleaseTestAttachmentDetails
                    });
                }).catch(error => {
                    if (error.response.status === 500) {
                        toast.error(error.message);
                    } else {
                        toast.warn(error.response.data);
                    }
                    this.setState({
                        loading: false
                    });
                });
        } else {
            toast.warn(this.props.intl.formatMessage({ id: "IDS_RECORDALREADYRELEASED" }));
        }
    }

    onComboChange = (comboData, fieldName) => {
        let selectedRecord = this.state.selectedRecord || {};
        let lstRegistrationSampleArno = this.props.masterData.RegistrationSampleArno;
        let lstRegistrationTest = this.props.masterData.RegistrationTest;
        let RegistrationSampleArno = [];
        let RegistrationTest = [];
        let ntransactionsamplecode = "";
        let ntransactiontestcode = "";
        let ssamplearno = null;
        let stestsynonym = null;
        if (fieldName === "npreregno") {
            lstRegistrationSampleArno.map(item => {
                if (item.item.npreregno === comboData.value) {
                    RegistrationSampleArno.push(item);
                }
            });
            let npreregno = {
                item: comboData.item,
                label: comboData.label,
                value: comboData.value
            };
            let sarno = comboData.item.sarno;
            selectedRecord = {
                ...selectedRecord, RegistrationSampleArno, RegistrationTest, npreregno
                , sarno, ntransactionsamplecode, ntransactiontestcode, ssamplearno, stestsynonym
            };
            this.setState({ selectedRecord });
        } else if (fieldName === "ntransactionsamplecode") {
            lstRegistrationTest.map(item => {
                if (item.item.ntransactionsamplecode === comboData.value) {
                    RegistrationTest.push(item);
                }
            });
            ntransactionsamplecode = {
                item: comboData.item,
                label: comboData.label,
                value: comboData.value
            }
            ssamplearno = comboData.item.ssamplearno;
            selectedRecord = {
                ...selectedRecord, RegistrationTest, ntransactionsamplecode
                , ssamplearno, ntransactiontestcode, stestsynonym
            };
            this.setState({ selectedRecord });
        } else if (fieldName === "ntransactiontestcode") {
            ntransactiontestcode = {
                item: comboData.item,
                label: comboData.label,
                value: comboData.value
            }
            stestsynonym = comboData.item.stestsynonym;
            selectedRecord = {
                ...selectedRecord, ntransactiontestcode
                , stestsynonym
            }
            this.setState({ selectedRecord });
        }
    }

    onInputOnChange = (event) => {
        const selectedRecord = this.state.selectedRecord || {};
        selectedRecord[event.target.name] = event.target.value;
        if (event.target.type === 'checkbox') {
            if (event.target.name === "nneedreport") {
                selectedRecord[event.target.name] = event.target.checked === true ? transactionStatus.YES : transactionStatus.NO;
            }
        }
        else {
            selectedRecord[event.target.name] = event.target.value;
        }
        if (selectedRecord["nneedreport"] && selectedRecord["nneedreport"] === transactionStatus.YES && selectedRecord["sfilename"] !== undefined && selectedRecord["sfilename"].length > 0) {
            let fileFormat = false;
            let requiredFileFormat = this.props.settings && this.props.settings[32] && this.props.settings[32].split(",");
            let fileNameSplit = selectedRecord["sfilename"] && Array.isArray(selectedRecord["sfilename"]) ? selectedRecord["sfilename"][0].name.split(".") : selectedRecord["sfilename"].split(".");
            requiredFileFormat.map(item => {
                if (item.slice(1) === fileNameSplit[fileNameSplit.length - 1]) {
                    fileFormat = true;
                }
            });
            selectedRecord["nneedreport"] = fileFormat ? selectedRecord["nneedreport"] : transactionStatus.NO;
            !fileFormat && toast.warn(this.props.intl.formatMessage({ id: "IDS_REPORTSUPPORTSONLYFILEFORMATS" }));
        }
        this.setState({ selectedRecord })
    }

    onDrop = (attachedFiles, fieldName, maxSize) => {
        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = onDropAttachFileList(selectedRecord[fieldName], attachedFiles, maxSize)
        this.setState({ selectedRecord });
    }

    deleteAttachment = (event, file, fieldName) => {
        let selectedRecord = this.state.selectedRecord || {};
        selectedRecord[fieldName] = deleteAttachmentDropZone(selectedRecord[fieldName], file)
        this.setState({
            selectedRecord
        });
    }

    dataStateChange = (event) => {
        this.setState({ dataState: event.dataState, dataResult: process(this.state.ReleaseTestAttachmentDetails, event.dataState) })
    }

    viewReleaseTestAttachmentFile = (filedata) => {
        delete (filedata.inputData.userinfo);
        const inputParam = {
            inputData: {
                releasetestattachment: filedata.inputData,
                userinfo: this.props.userInfo
            },
            classUrl: "release",
            operation: "view",
            methodUrl: "ReleaseTestAttachment",
        }
        this.props.viewAttachment(inputParam);
    }

    onSaveClick = (saveType, formRef) => {
        const formData = new FormData();
        const selectedRecord = this.state.selectedRecord;
        const acceptedFiles = selectedRecord.sfilename;
        const userInfo = this.props.userInfo;
        let isFileEdited = transactionStatus.NO;
        let fileArray = [];
        if (acceptedFiles && Array.isArray(acceptedFiles) && acceptedFiles.length > 0) {
            if (this.state.operation === 'create') {
                acceptedFiles.forEach((file, index) => {
                    const splittedFileName = file.name.split('.');
                    const fileExtension = file.name.split('.')[splittedFileName.length - 1];
                    const fileName = selectedRecord.ssystemfilename && selectedRecord.ssystemfilename !== "" ?
                        selectedRecord.ssystemfilename.split('.')[0] : create_UUID();
                    const uniquefilename = fileName + '.' + fileExtension;
                    const tempData = {};
                    tempData["nformcode"] = userInfo.nformcode;
                    tempData["npreregno"] = selectedRecord.npreregno.value;
                    tempData["ntransactionsamplecode"] = selectedRecord.ntransactionsamplecode.value;
                    tempData["ntransactiontestcode"] = selectedRecord.ntransactiontestcode.value;
                    tempData["ncoaparentcode"] = this.props.masterData.ncoaparentcode;
                    tempData["nusercode"] = userInfo.nusercode;
                    tempData["nuserrolecode"] = userInfo.nuserrole;
                    tempData["jsondata"] = {
                        stestsynonym: selectedRecord.ntransactiontestcode.label,
                        susername: userInfo.susername,
                        suserrolename: userInfo.suserrolename,
                        nfilesize: file.size,
                        ssystemfilename: uniquefilename,
                        sfilename: Lims_JSON_stringify(file.name.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false),
                        slinkname: "",
                        sdescription: Lims_JSON_stringify(selectedRecord.sdescription ? selectedRecord.sdescription.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim() : "", false),
                        nneedreport: selectedRecord.nneedreport ? selectedRecord.nneedreport : transactionStatus.NO
                    }
                    formData.append("uploadedFile" + index, file);
                    formData.append("uniquefilename" + index, uniquefilename);
                    fileArray.push(tempData);
                });
                formData.append("filecount", acceptedFiles.length);
                isFileEdited = transactionStatus.YES;
            } else {
                acceptedFiles.forEach((file, index) => {
                    const splittedFileName = file.name.split('.');
                    const fileExtension = file.name.split('.')[splittedFileName.length - 1];
                    const fileName = selectedRecord.ssystemfilename && selectedRecord.ssystemfilename !== "" ?
                        selectedRecord.ssystemfilename.split('.')[0] : create_UUID();
                    const uniquefilename = fileName + '.' + fileExtension;
                    const tempData = {};
                    tempData["nreleasetestattachmentcode"] = selectedRecord.selectedReleaseTestAttachment && selectedRecord.selectedReleaseTestAttachment.nreleasetestattachmentcode;
                    tempData["nformcode"] = userInfo.nformcode;
                    tempData["npreregno"] = selectedRecord.npreregno.value;
                    tempData["ntransactionsamplecode"] = selectedRecord.ntransactionsamplecode.value;
                    tempData["ntransactiontestcode"] = selectedRecord.ntransactiontestcode.value;
                    tempData["ncoaparentcode"] = this.props.masterData.ncoaparentcode;
                    tempData["nusercode"] = userInfo.nusercode;
                    tempData["nuserrolecode"] = userInfo.nuserrole;
                    tempData["jsondata"] = {
                        stestsynonym: selectedRecord.ntransactiontestcode.label,
                        susername: userInfo.susername,
                        suserrolename: userInfo.suserrolename,
                        nfilesize: file.size,
                        ssystemfilename: uniquefilename,
                        sfilename: Lims_JSON_stringify(file.name.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false),
                        slinkname: "",
                        sdescription: Lims_JSON_stringify(selectedRecord.sdescription ? selectedRecord.sdescription.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim() : "", false),
                        nneedreport: selectedRecord.nneedreport ? selectedRecord.nneedreport : transactionStatus.NO
                    }
                    formData.append("uploadedFile" + index, file);
                    formData.append("uniquefilename" + index, uniquefilename);
                    formData.append("filecount", acceptedFiles.length);
                    isFileEdited = transactionStatus.YES;

                    fileArray.push(tempData);
                });
            }
        } else {
            let tempData = {};
            tempData["nreleasetestattachmentcode"] = selectedRecord.selectedReleaseTestAttachment && selectedRecord.selectedReleaseTestAttachment.nreleasetestattachmentcode;
            tempData["nformcode"] = userInfo.nformcode;
            tempData["npreregno"] = selectedRecord.npreregno.value;
            tempData["ntransactionsamplecode"] = selectedRecord.ntransactionsamplecode.value;
            tempData["ntransactiontestcode"] = selectedRecord.ntransactiontestcode.value;
            tempData["ncoaparentcode"] = this.props.masterData.ncoaparentcode;
            tempData["nusercode"] = userInfo.nusercode;
            tempData["nuserrolecode"] = userInfo.nuserrole;
            tempData["jsondata"] = {
                stestsynonym: selectedRecord.ntransactiontestcode.label,
                susername: userInfo.susername,
                suserrolename: userInfo.suserrolename,
                nfilesize: selectedRecord.size,
                ssystemfilename: selectedRecord.ssystemfilename,
                sfilename: Lims_JSON_stringify(acceptedFiles.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false),
                slinkname: "",
                sdescription: Lims_JSON_stringify(selectedRecord.sdescription ? selectedRecord.sdescription.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim() : "", false),
                nneedreport: selectedRecord.nneedreport ? selectedRecord.nneedreport : transactionStatus.NO
            }
            fileArray.push(tempData);
        }
        formData.append("isFileEdited", isFileEdited);
        formData.append("releasetestattachment", JSON.stringify(fileArray));
        formData.append("nattachmenttypecode", attachmentType.FTP);
        formData.append("ncoaparentcode", this.props.masterData.ncoaparentcode);
        formData.append("userinfo", JSON.stringify({
            ...userInfo,
            sformname: Lims_JSON_stringify(userInfo.sformname),
            smodulename: Lims_JSON_stringify(userInfo.smodulename),
            slanguagename: Lims_JSON_stringify(userInfo.slanguagename)
        }));

        const inputParam = {
            inputData: {
                "userinfo": {
                    ...userInfo,
                    sformname: Lims_JSON_stringify(userInfo.sformname),
                    smodulename: Lims_JSON_stringify(userInfo.smodulename),
                    slanguagename: Lims_JSON_stringify(userInfo.slanguagename)
                }
            },
            formData: formData,
            isFileupload: true,
            operation: this.state.operation,
            classUrl: "release",
            saveType: saveType,
            formRef: formRef,
            methodUrl: "ReleaseTestAttachment"
        };
        formData.append("userinfo", JSON.stringify(inputParam.inputData.userinfo));

        const requestUrl = rsapi.post("release/" + this.state.operation + "ReleaseTestAttachment", formData);
        return requestUrl
            .then(response => {
                let ReleaseTestAttachmentDetails = response.data.ReleaseTestAttachmentDetails;
                let selectedRecord = {};
                let openModal = false;
                this.setState({ selectedRecord, openModal, ReleaseTestAttachmentDetails });

            }).catch(error => {
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
                this.setState({
                    loading: false
                });
            });
    }

}

export default connect(null, { viewAttachment })(injectIntl(ReleaseTestAttachment));
