export function getApprovedSample(inputParam, ncontrolCode) {
  return function (dispatch) {
    let urlArray = [];
    inputParam.inputData['isAddPopup'] = true;
    const releaseSample = rsapi.post("release/getReleaseSample", inputParam.inputData); //  const reportType = rsapi.post("release/getCOAReportType", { userinfo: inputParam.inputData.userinfo });

    const projectType = rsapi.post("release/getApprovedProjectType", inputParam.inputData);
    const sectionType = rsapi.post("release/getSection", inputParam.inputData);

    if (inputParam.inputData.ncoareporttypecode === reportCOAType.PROJECTWISE || inputParam.inputData.nsampletypecode === SampleType.PROJECTSAMPLETYPE && inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISEMULTIPLESAMPLE) {
      urlArray = [projectType];
    } else if (inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISE || inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISEMULTIPLESAMPLE) {
      urlArray = [sectionType];
    } else {
      urlArray = [releaseSample //,reportType
      ];
    }

    dispatch(initRequest(true));
    Axios.all(urlArray).then(response => {
      // const reportTypeMap = constructOptionList(response[1].data.ReportType || [], "ncoareporttypecode",
      //     "scoareporttypename", undefined, undefined, false);
      // const ReportTypeList = reportTypeMap.get("OptionList");
      let ProjectTypeList = [];
      let sectionList = []; // let selectedRecord = {"ncoareporttypecode": {
      //     "value": response[1].data.ReportType[0].ncoareporttypecode,
      //      "label": response[1].data.ReportType[0].scoareporttypename
      // }
      // };

      let masterData = {};
      let openModal = true;
      let selectedRecord = inputParam.inputData.selectedRecord;

      if (inputParam.inputData.ncoareporttypecode === reportCOAType.PROJECTWISE || inputParam.inputData.nsampletypecode === SampleType.PROJECTSAMPLETYPE && inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISEMULTIPLESAMPLE) {
        const projectTypeMap = constructOptionList(response[0].data || [], "nprojecttypecode", "sprojecttypename", undefined, undefined, false);
        ProjectTypeList = projectTypeMap.get("OptionList");
        masterData = { ...inputParam.masterData,
          ReleaseSubSample: [],
          ReleaseTest: [],
          ReleaseSample: []
        };

        if (inputParam.inputData.selectedRecord.nprojecttypecode) {
          delete inputParam.inputData.selectedRecord.nprojecttypecode;
        }

        if (inputParam.inputData.selectedRecord.nprojectmastercode) {
          delete inputParam.inputData.selectedRecord.nprojectmastercode;
        }
      } else if (inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISE || inputParam.inputData.ncoareporttypecode === reportCOAType.SECTIONWISEMULTIPLESAMPLE) {
        const sectionTypeMap = constructOptionList(response[0].data.sectionList || [], "nsectioncode", "ssectionname", undefined, undefined, false);
        sectionList = sectionTypeMap.get("OptionList");
        masterData = { ...inputParam.masterData,
          ReleaseSubSample: [],
          ReleaseTest: [],
          ReleaseSample: []
        };

        if (inputParam.inputData.selectedRecord.nsectioncode) {
          delete inputParam.inputData.selectedRecord.nsectioncode;
        }
      } else {
        if (response[0].data && response[0].data.ReleaseSample.length !== 0) {
          let responseData = { ...response[0].data
          };
          masterData = { ...inputParam.masterData,
            ...responseData
          };
        } else {
          let responseData = { ...response[0].data
          };
          masterData = { ...inputParam.masterData,
            ...responseData
          };
          openModal = false;
          toast.warn(intl.formatMessage({
            id: "IDS_NOSAMPLESAREAVAILABLE"
          }));
        }
      }

      sortData(masterData);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          masterData,
          expandCheck: false,
          ProjectTypeList,
          ncontrolCode,
          isDeletePopup: false,
          isComboCheck: false,
          isEditPopup: false,
          screenName: inputParam.inputData.screenName,
          sectionList,
          loading: false,
          showFilter: false,
          openModal: openModal,
          isAddPopup: true,
          operation: "save"
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
export function generateReleasedReport(inputData, Data) {
  return function (dispatch) {
    dispatch(initRequest(true));
    let urlArray = []; //   const releaseSample = rsapi.post("release/updateRelease", inputData)  ;
    //   const afterCorrection = rsapi.post("release/updateReleaseAfterCorrection", inputData)  ;

    if (inputData.listStatus === transactionStatus.CORRECTION) {
      const afterCorrection = rsapi.post("release/updateReleaseAfterCorrection", inputData);
      urlArray = [afterCorrection];
    } else {
      const releaseSample = rsapi.post("release/updateRelease", inputData);
      urlArray = [releaseSample];
    }

    Axios.all(urlArray).then(response => {
      if (response[0].data["PortalStatus"] && response[0].data["PortalStatus"].length > 0) {
        dispatch(UpdateExternalOrderStatus(response[0].data["PortalStatus"], inputData));
      }

      let masterData = { ...Data,
        ...response[0].data
      };

      if (response[0].data.rtn === "Success") {
        document.getElementById("download_data").setAttribute("href", response[0].data.filepath);
        document.getElementById("download_data").click();
      } else {
        toast.warn(response[0].data.rtn);
      }

      sortData(masterData.ReleaseHistory, "", 'ncoaparentcode');
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false,
          change: inputData.change,
          masterData,
          modalShow: false,
          selectedRecord: {},
          loadEsign: false,
          openModal: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
