export function getRegTypeJobAllocation(inputData) {
  return function (dispatch) {
    dispatch(initRequest(true));
    rsapi.post("joballocation/getRegistrationTypeBySampleType", inputData).then(response => {
      let responseData = { ...response.data
      };
      responseData = sortData(responseData);
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          masterData: { ...inputData.masterData,
            ...responseData,
            defaultSampleTypeValue: inputData.defaultSampleTypeValue
          },
          loading: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
