export function getInstrument(ninstrumentcatcode, ncalibrationreq, userInfo, selectedRecord) {
  return function (dispatch) {
    dispatch(initRequest(true));
    return rsapi.post("joballocation/getInstrumentBasedCategory", {
      "ninstrumentcatcode": ninstrumentcatcode,
      "ncalibrationreq": ncalibrationreq,
      "userinfo": userInfo
    }).then(response => {
      let Instrument = [];
      const InstrumentMap = constructOptionList(response.data.Instrument || [], "ninstrumentcode", "sinstrumentid", undefined, undefined, false);
      Instrument = InstrumentMap.get("OptionList");

      if (ninstrumentcatcode !== transactionStatus.NA) {
        selectedRecord["dinstblockfromdatetime"] = new Date();
      }

      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          Instrument,
          selectedRecord,
          loading: false
        }
      });
    }).catch(error => {
      dispatch({
        type: DEFAULT_RETURN,
        payload: {
          loading: false
        }
      });

      if (error.response.status === 500) {
        toast.error(error.message);
      } else {
        toast.warn(error.response.data);
      }
    });
  };
}
