          testskip,
          testtake
        } = inputData;
        let bool = false;
        let skipInfo = {};

        if (inputData.masterData.JA_TEST.length <= inputData.testskip) {
          testskip = 0;
          bool = true;
        }

        if (bool) {
          skipInfo = {
            testskip,
            testtake
          };
