import React from 'react';
import NumericInput from 'react-numeric-input';
import { Form } from 'react-bootstrap';
import { whitespaceTrim } from '../CommonScript';

function handleFocus(e){
    e.target.select();
}
const FormNumericInput = ({
    name,
    type,
    placeholder,
    onChange,
    onKeyUp,
    className,
    value,
    isMandatory,
    children,
    label,
    // isInvalid,
    onBlur,
    errors,
    strict,
    step,
    min,
    max,
    noStyle,
    maxLength,
    precision,
    snap,
    isDisabled,
    required,
    ...props
}) => (
    <React.Fragment>
         <Form.Group className={`form-select w-100 react-select-wrap ${props.formGroupClassName? props.formGroupClassName: ""}`} controlId={name}>
            <Form.Label htmlFor={name}>{label}{isMandatory && <sup>*</sup>}</Form.Label>
               
            <NumericInput
                name={name}
                type={type}
                placeholder={placeholder}
                value={value}
                min={min}
                //onFocus={(e)=>handleFocus(e)}
                max={max}
                step={step}
                precision={precision}
                strict={strict}
                maxLength={maxLength}
                onChange={onChange}
                onKeyUp={onKeyUp}
                // isInvalid={isInvalid}
                onBlur={onBlur}
                snap={snap}
                noStyle={noStyle}
                className={className}
                disabled={isDisabled}
                required={false}
                autoComplete="off"
                onPaste={(e) => {
                    e.preventDefault()
                    return false;
                }}
            />
     
        </Form.Group>
    </React.Fragment>
);
FormNumericInput.defaultProps = {
    type: "number",
    className: ""
}
export default FormNumericInput;
