                    this.props.crudMaster(inputParam, masterData, "openModal", {});
                }
            } else {
                toast.warn(this.props.intl.formatMessage({ id: "IDS_SPECIFICATIONSTATUSMUSTBEDRAFTCORRECTION" }));
            }
        }
    }

