                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}

export function getChildValues(inputParem,
    userinfo, selectedRecord, comboData, parentcolumnlist,
    childcolumnlist, withoutCombocomponent, parentListWithReadonly,
    productCategory, product, mapOfRegFilterData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post('dynamicpreregdesign/getChildValues', {
            child: inputParem.child,
            parentdata: inputParem.item.jsondata,
            parentsource: inputParem.source,
            [inputParem.primarykeyField]: inputParem.value,
            valuemember: inputParem.primarykeyField,
            childcolumnlist,
            userinfo,
            parentcolumnlist
        })
            .then(response => {
                let returnObj = { ...comboData, ...response.data }
                returnObj = parentChildComboLoad(parentListWithReadonly, returnObj, selectedRecord, childcolumnlist, withoutCombocomponent, inputParem.item)
                selectedRecord = { ...selectedRecord, ...returnObj.selectedRecord }
                if (productCategory) {
                    // const nproductcatcode = selectedRecord['Product Category'] && selectedRecord['Product Category'].value
                    dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData }))
                } else if (product) {
                    // if (mapOfRegFilterData.nproductcode === -1) {
                    if (selectedRecord["Product"]) {
                        mapOfRegFilterData["nproductcode"] = selectedRecord["Product"] ? selectedRecord["Product"].value : -1
                        dispatch(getTreeByProduct(mapOfRegFilterData, selectedRecord, { ...returnObj.comboData }))
                    } else {
                        selectedRecord["nallottedspeccode"] = ""
                        selectedRecord["sversion"] = ""
                        dispatch({
                            type: DEFAULT_RETURN,
                            payload: {
                                loading: false,
                                comboData: { ...returnObj.comboData },
                                selectedRecord,
                                AgaramTree: [],
                                ActiveKey: [],
                                FocusKey: [],
                                OpenNodes: [],
                                Test: [],
                                SelectedTest: [],
                                selectedSpec: {},
                                Component: [],
                                selectComponent: {},
                                selectedComponent: {},
                                Specification: []
                            }
                        })
                        // }
                    }


                }
                else {
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            loading: false,
                            comboData: { ...returnObj.comboData },
                            selectedRecord
                        }
                    })
                }
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }
}
