import React from 'react';
import { Col, Nav, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FormattedMessage } from 'react-intl';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import DataGrid from '../../components/data-grid/data-grid.component';
import ReactTooltip from 'react-tooltip';


const InvenotryTransaction = (props) => {
    const QuantityTransaction = props.controlMap.has("QuantityTransaction") && props.controlMap.get("QuantityTransaction").ncontrolcode;
    const editId = props.controlMap.has("EditMaterialSection") && props.controlMap.get("EditMaterialSection").ncontrolcode;
    const testSectionColumnList = [
        { "idsName": "IDS_QUANTITYTRANSACTION", "dataField": "IDS_INVENTORYID", "width": "200px" },
        {
            "idsName": "dtransactiondate", "dataField": "dtransactiondate", "width": "100px"
        }
    ];
  //  const methodUrl = "MaterialSection";
    let primaryKeyField = "nmaterialsectioncode";
    // const editParam = {
    //     operation: "update", primaryKeyField, userInfo: props.userInfo, ncontrolCode: editId,
    //     masterData: props.masterData, selectedRecord: props.selectedRecord
    // }


    //const inputParam = { classUrl: "material", methodUrl: "getActiveMaterialSectionById" }
    return (
        <>
            <div className="actions-stripe">
                <div className="d-flex justify-content-end">
                    <ReactTooltip place="bottom" globalEventOff='click' />
                    <Nav.Link name="QuantityTransaction" className="add-txt-btn" hidden={props.userRoleControlRights.indexOf(QuantityTransaction) === -1}
                        onClick={() => props.quantityTransaction(QuantityTransaction, "create", {}, props.userInfo)}>
                        <FontAwesomeIcon icon={faPlus} /> { }
                        <FormattedMessage id="IDS_QUANTITYTRANSACTION" defaultMessage="Quantity Transaction" />
                    </Nav.Link>
                </div>
            </div>
            <Row noGutters={true}>
                <Col md="12">

                    <DataGrid
                        key="testsectionkey"
                        primaryKeyField="nmaterialinventtranscode"
                        dataResult={props.dataResult}
                        dataState={props.dataState}
                        dataStateChange={props.dataStateChange}
                        extractedColumnList={testSectionColumnList}
                        controlMap={props.controlMap}
                        userRoleControlRights={props.userRoleControlRights}
                       // inputParam={inputParam}
                       // editParam={editParam}
                        fetchRecord={props.fetchRecord}
                        deleteRecord={props.deleteRecord}
                        pageable={false}
                        scrollable={'scrollable'}
                        gridHeight={'600px'}
                        isActionRequired={true}
                       // deleteParam={{ operation: "delete", methodUrl }}
                       // methodUrl={methodUrl}
                        hideColumnFilter={false}
                        selectedId={0}
                        isJsonFeild={true}
                        jsonFeild='jsondata'
                    >
                    </DataGrid>

                </Col>
            </Row>
        </>
    );
};

export default InvenotryTransaction;
