]["agree"];inputParam["screenData"]["inputParam"]["inputData"]["userinfo"]=inputParam.inputData.userinfo;dispatch(updateDecision(inputParam["screenData"]["inputParam"]));}}}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function previewSampleReport(inputParam){return function(dispatch){dispatch(initRequest(true));var ndecisionStatus=inputParam.sample.ndecisionstatus;if(inputParam.sample.ndecisionstatus===undefined||inputParam.sample.ndecisionstatus===transactionStatus.DRAFT){ndecisionStatus=transactionStatus.PASS;}var inputData={ndecisionstatus:ndecisionStatus,userinfo:inputParam.userinfo,nprimarykey:inputParam.sample.npreregno,ncoareporttypecode:reportCOAType.SAMPLECERTIFICATEPRIVIEW,nreporttypecode:REPORTTYPE.SAMPLEREPORT,sprimarykeyname:"npreregno",ncontrolcode:inputParam.ncontrolCode,nregtypecode:inputParam.sample.nregtypecode,nregsubtypecode:inputParam.sample.nregsubtypecode,npreregno:inputParam.sample.npreregno};rsapi.post("approval/previewSampleReport",inputData).then(function(response){if(response.data.rtn==="Success"){document.getElementById("download_data").setAttribute("href",response.data.filepath);document.getElementById("download_data").click();}else{toast.warn(response.data.rtn);}dispatch({type:DEFAULT_RETURN,payload:{loading:false,openChildModal:false,loadEsign:false}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function generateCOAReport(inputParam){return function(dispatch){dispatch(initRequest(true));var inputData={npreregno:inputParam.sample.npreregno,nsectioncode:inputParam.nsectioncode||-1,userinfo:inputParam.userinfo,nprimarykey:inputParam.sample.npreregno,ncoareporttypecode:reportCOAType.SAMPLEWISE,nreporttypecode:REPORTTYPE.COAREPORT,sprimarykeyname:"npreregno",ncontrolcode:inputParam.ncontrolCode,nregtypecode:inputParam.nregtypecode,nregsubtypecode:inputParam.nregsubtypecode};rsapi.post("approval/generateCOAReport",inputData).then(function(response){if(response.data.rtn==="Success"){document.getElementById("download_data").setAttribute("href",response.data.filepath);document.getElementById("download_data").click();}else{toast.warn(response.data.rtn);}dispatch({type:DEFAULT_RETURN,payload:{loading:false,openChildModal:false,selectedRecord:{},loadEsign:false}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function getEnforceCommentsHistory(selectedParam,masterData,userInfo){return function(dispatch){dispatch(initRequest(true));rsapi.post("approval/getEnforceCommentsHistory",{ntransactionresultcode:selectedParam.ntransactionresultcode,userinfo:userInfo}).then(function(response){if(response.data.length>0){masterData=_objectSpread(_objectSpread({},masterData),{},{enforceCommentsHistory:response.data});dispatch({type:DEFAULT_RETURN,payload:{loading:false,masterData:masterData,openChildModal:true,operation:"view",screenName:"IDS_ENFORCECOMMENTHISTORY"}});}else{dispatch({type:DEFAULT_RETURN,payload:{loading:false,multilingualMsg:"IDS_NOPREVIOUSCOMMENTSFOUND"}});}}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}

