      } else {
        selectedRecord[event.target.name] = event.target.value;
      }

      this.setState({
        selectedRecord
      });
    };

    this.onInputOnChange = (event, caseNo, optional) => {
      const selectedRecord = this.state.selectedRecord || {};

      switch (caseNo) {
        case 1:
          if (event.target.type === 'checkbox') {
            selectedRecord[event.target.name] = event.target.checked === true ? transactionStatus.YES : transactionStatus.NO;
          } else if (event.target.type === 'radio') {
            selectedRecord[event.target.name] = optional; //  selectedRecord["sfilename"]="";
          } else {
            selectedRecord[event.target.name] = event.target.value;
          }

          this.setState({
            selectedRecord
          });
          break;

        case 4:
          const inputValue = event.target.value;

          if (/^-?\d*?\.?\d*?$/.test(inputValue) || inputValue === "") {
            selectedRecord[event.target.name] = event.target.value;
          }

          this.setState({
            selectedRecord
          });
          break;

        case 5:
          if (optional.ndynamicformulafieldcode === FORMULAFIELDTYPE.INTEGER && optional.sdescription.indexOf('P$') === -1) {
            const value = event.target.value.replace(/[^-^0-9]/g, '');
            const dynamicField = this.state.dynamicField || [];

            if (/^-?\d*?$/.test(value)) {
              if (!selectedRecord['formulainput']) {
                selectedRecord['formulainput'] = {};
              }

              selectedRecord['formulainput'][event.target.name] = value;
              dynamicField[event.target.name] = {
                sparameter: optional.sdescription,
                svalues: value
              };
            } else if (value === "") {
              if (!selectedRecord['formulainput']) {
                selectedRecord['formulainput'] = {};
              }

              selectedRecord['formulainput'][event.target.name] = value;
              dynamicField[event.target.name] = {
                sparameter: optional.sdescription,
                svalues: value
              };
            }

            this.setState({
              dynamicField,
              selectedRecord
            });
            break;
          } else {
            const value = event.target.value.replace(/[^-^0-9.]/g, '');
            const dynamicField = this.state.dynamicField || [];

            if (/^-?\d*?\.?\d*?$/.test(value) || value !== "") {
              if (!selectedRecord['formulainput']) {
                selectedRecord['formulainput'] = {};
              }

              selectedRecord['formulainput'][event.target.name] = value;
              dynamicField[event.target.name] = {
                sparameter: optional.sdescription,
                svalues: value
              };
            }

            this.setState({
              dynamicField,
              selectedRecord
            });
            break;
          }

        default:
          break;
      }
