import React from 'react';
import { AtTableWrap } from '../client-group.styles';
import { FormattedMessage, injectIntl } from 'react-intl';
import { Grid, GridColumn as Column, GridNoRecords } from '@progress/kendo-react-grid';
import { process } from '@progress/kendo-data-query';
import ReactTooltip from 'react-tooltip';
import { Card, Col, FormGroup, FormLabel, Row } from 'react-bootstrap';
import { FormControlStatic } from './data-grid.styles';
import { parse } from '@fortawesome/fontawesome-svg-core';
import SimpleGrid from './SimpleGrid';

class SimpleGridWithSelection extends React.Component {
    constructor(props) {
        super(props);

        this.subChildColumnList = [{ "idsName": "IDS_TESTNAME", "dataField": "ssamplearno", "width": "150px", "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox" },
            // { "idsName": "IDS_SAMPLENAME", "dataField": "Sample Name", "width": "100px" , "mandatoryLabel":"IDS_SELECT", "controlType": "selectbox"},
            //  { "idsName": "IDS_CONTAINERTYPE", "dataField": "Container Type", "width": "100px" , "mandatoryLabel":"IDS_SELECT", "controlType": "selectbox"}
        ];
    }
    expandChange = (event) => {
        const isExpanded =
            event.dataItem.expanded === undefined ?
                event.dataItem.aggregates : event.dataItem.expanded;

        if (this.props.hasChild && event.value === true) {
            event.dataItem.expanded = !isExpanded;
            this.props.handleExpandChange(event, this.props.dataState)
        }
        else {
            event.dataItem.expanded = !isExpanded;
            this.setState({ isExpanded });
        }
    }
    detailBand = (props) => {
        return (
            <Row bsPrefix="margin_class">
                <Col md={12}>
                    {this.props.hasDynamicColSize ?
                        <Card>
                            <Card.Header><FormattedMessage id="IDS_MOREINFO" message="More Info" /></Card.Header>
                            <Card.Body className="form-static-wrap">
                                <Row style={{ marginLeft: -18 }}>
                                    {this.props.detailedFieldList.map((item) => {
                                        return (

                                            <Col md={item.columnSize}>
                                                <FormGroup>
                                                    <FormLabel><FormattedMessage id={item.idsName} message={item.idsName} /></FormLabel>
                                                    <FormControlStatic>
                                                        {
                                                            props.dataItem[item.dataField] === undefined ||
                                                                props.dataItem[item.dataField] === null ||
                                                                props.dataItem[item.dataField].length === 0 ? '-' :
                                                                item.isIdsField ? <FormattedMessage id={props.dataItem[item.dataField]} message={props.dataItem[item.dataField]} /> :
                                                                    item.isHTML ? parse(props.dataItem[item.dataField]) : props.dataItem[item.dataField]}
                                                    </FormControlStatic>
                                                </FormGroup>
                                            </Col>
                                        )
                                    })}
                                </Row>
                            </Card.Body>
                        </Card>
                        : this.props.hasSubChild ?
                            <SimpleGrid dataResult1={this.props.dataResult1 && this.props.dataResult1 || []}
                                extractedColumnList={this.subChildColumnList} />
                            :
                            <Card>
                                <Card.Header><FormattedMessage id="IDS_MOREINFO" message="More Info" /></Card.Header>
                                <Card.Body className="form-static-wrap">
                                    <Row>
                                        {this.props.detailedFieldList.map((item) => {
                                            return (

                                                <Col md={6}>
                                                    <FormGroup>
                                                        <FormLabel><FormattedMessage id={item.idsName} message={item.idsName} /></FormLabel>
                                                        <FormControlStatic>
                                                            {props.dataItem[item.dataField] === undefined || props.dataItem[item.dataField] === null || props.dataItem[item.dataField].length === 0 ? '-' :
                                                                item.isIdsField ? <FormattedMessage id={props.dataItem[item.dataField]} message={props.dataItem[item.dataField]} /> : item.isHTML ? parse(props.dataItem[item.dataField]) : props.dataItem[item.dataField]}
                                                        </FormControlStatic>
                                                    </FormGroup>
                                                </Col>
                                            )
                                        })}
                                    </Row>
                                </Card.Body>
                            </Card>}
                </Col> </Row>
        )
    }
    render() {
        return (

            <AtTableWrap className="at-list-table">
                <ReactTooltip place="bottom" globalEventOff='click' id="tooltip-samplegrid-wrap" />
                {/* <Tooltip openDelay={100} position="auto" tooltipClassName="ad-tooltip" anchorElement="element" parentTitle={true}> */}
                <Grid
                    data={process(this.props.dataResult1, { skip: 0, take: this.props.dataResult1.length })}
                    selectedField="selected"
                    expandField={this.props.expandField ? this.props.expandField : false}
                    detail={this.props.hideDetailBand ? false : this.detailBand}
                    onExpandChange={this.expandChange}
                    onSelectionChange={this.props.selectionChange}
                    onHeaderSelectionChange={this.props.headerSelectionChange}
                    scrollable={this.props.scrollable}

                     onRowClick={this.props.rowClick}>
                    <Column
                        field="selected"
                        width="50px"
                        title={this.props.title}
                        headerSelectionValue={this.props.selectAll}
                    />
                    {/* <Column type='checkbox' width='50'></Column> */}

                    {this.props.extractedColumnList.map((item, index) =>
                        <Column key={index}
                            title={this.props.intl.formatMessage({ id: item.idsName })}
                            cell={(row) => (
                                <td data-for="tooltip-samplegrid-wrap"
                                    data-tip={row["dataItem"][item.dataField]}>
                                    {/* title={row["dataItem"][item.dataField]}> */}
                                    {item.isIdsField ? <FormattedMessage id={row["dataItem"][item.dataField]} defaultMessage={row["dataItem"][item.dataField]} />
                                        : row["dataItem"][item.dataField]}
                                </td>)}
                        />
                    )}
                </Grid>
                {/* </Tooltip>           */}
            </AtTableWrap>
        );
    }
}
export default injectIntl(SimpleGridWithSelection);
