  componentDidMount() {
    if (this.parentHeight) {
      const height = this.parentHeight.clientHeight;
      this.setState({
        firstPane: height - 80,
        parentHeight: height
      });
    }
  }

  generateBreadCrumData() {
    const breadCrumbData = [];
    breadCrumbData.push({
      "label": "IDS_MATERIALTYPE",
      "value": this.props.Login.masterData.SelectedMaterialType ? this.props.Login.masterData.SelectedMaterialType[0]['jsondata'].smaterialtypename[this.props.Login.userInfo.slanguagetypecode] : "NA"
    }, {
      "label": "IDS_MATERIALCAT",
      "value": this.props.Login.masterData.SelectedMaterialCategory ? this.props.Login.masterData.SelectedMaterialCategory.smaterialcatname !== undefined ? this.props.Login.masterData.SelectedMaterialCategory.smaterialcatname : "NA" : "NA"
    }, {
      "label": "IDS_MATERIAL",
      "value": this.props.Login.masterData.SelectedMaterialCrumb ? this.props.Login.masterData.SelectedMaterialCrumb['jsondata'].smaterialname !== undefined ? this.props.Login.masterData.SelectedMaterialCrumb['jsondata'].smaterialname : "NA" : "NA"
    });
    return breadCrumbData;
  }

  componentDidUpdate(previousProps) {
    let bool = false;
    let {
      selectedRecord,
      selectedcombo,
      comboitem,
      filterData,
      filterCatList,
      selectedMaterialCat,
      selectedMaterialcombo,
      materialCatList,
      MaterialComboList,
      SelectedMaterialInventory
    } = this.state;
    let masterData = this.props.Login.masterData;

    if (this.props.Login.selectedRecord !== previousProps.Login.selectedRecord) {
      bool = true;
      selectedRecord = this.props.Login.selectedRecord;
    }

    if (this.props.Login.selectedMaterialCat !== previousProps.Login.selectedMaterialCat) {
      bool = true;
      selectedMaterialCat = this.props.Login.selectedMaterialCat;
    }

    if (this.props.Login.masterData.SelectedMaterialInventory !== previousProps.Login.masterData.SelectedMaterialInventory) {
      bool = true;
      SelectedMaterialInventory = this.props.Login.SelectedMaterialInventory;
    }

    if (this.props.Login.comboitem !== previousProps.Login.comboitem) {
      bool = true;
      comboitem = this.props.Login.comboitem;
    }

    if (this.props.Login.userInfo.nformcode !== previousProps.Login.userInfo.nformcode) {
      const userRoleControlRights = [];

      if (this.props.Login.userRoleControlRights) {
        this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode] && Object.values(this.props.Login.userRoleControlRights[this.props.Login.userInfo.nformcode]).map(item => userRoleControlRights.push(item.ncontrolcode));
      }

      const controlMap = getControlMap(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode);
      this.setState({
        userRoleControlRights,
        controlMap,
        data: this.props.Login.masterData.ControlRights
      });
    }

    if (this.props.Login.masterData.MaterialType !== undefined) {
      if (this.props.Login.masterData.MaterialType !== previousProps.Login.masterData.MaterialType) {
        bool = true;
        const materialTypeList = constructjsonOptionList(this.props.Login.masterData.MaterialType, "nmaterialtypecode", "smaterialtypename", undefined, undefined, undefined, undefined, undefined, true, this.props.Login.userInfo.slanguagetypecode);
        filterCatList = materialTypeList.get("OptionList");
        selectedcombo = {
          nmaterialtypecode: masterData.MaterialType && masterData.MaterialType.length > 0 ? {
            "value": masterData.MaterialType[0].nmaterialtypecode,
            "label": masterData.MaterialType[0]['jsondata'].smaterialtypename[this.props.Login.userInfo.slanguagetypecode]
          } : ""
        };
      }
    }

    if (this.props.Login.masterData.MaterialCategoryMain !== previousProps.Login.masterData.MaterialCategoryMain) {
      bool = true;
      const MaterialCategoryMain = constructOptionList(this.props.Login.masterData.MaterialCategoryMain || [], "nmaterialcatcode", "smaterialcatname", undefined, undefined, undefined);
      materialCatList = MaterialCategoryMain.get("OptionList");
      selectedMaterialCat = {
        nmaterialcatcode: masterData.MaterialCategoryMain && masterData.MaterialCategoryMain.length > 0 ? {
          "value": masterData.MaterialCategoryMain[0].nmaterialcatcode,
          "label": masterData.MaterialCategoryMain[0].smaterialcatname
        } : ""
      };
    }

    if (this.props.Login.masterData.MaterialCombo !== previousProps.Login.masterData.MaterialCombo) {
      bool = true;
      let MaterialComboArr = [];
      this.props.Login.masterData.MaterialCombo.map(temp => {
        MaterialComboArr.push(temp.jsondata);
      });
      const MaterialCombo = constructOptionList(MaterialComboArr || [], "nmaterialcode", "smaterialname", undefined, undefined, undefined);
      MaterialComboList = MaterialCombo.get("OptionList");
      selectedMaterialcombo = {
        nmaterialcode: masterData.MaterialCombo && masterData.MaterialCombo.length > 0 ? {
          "value": masterData.MaterialCombo[0]["jsondata"].nmaterialcode,
          "label": masterData.MaterialCombo[0]["jsondata"].smaterialname
        } : ""
      };
    }

    if (this.props.Login.masterData !== previousProps.Login.masterData) {
      bool = true;
      filterData = this.generateBreadCrumData();
    }

    if (this.props.Login.masterData.searchedData !== previousProps.Login.masterData.searchedData) {
      if (this.props.Login.masterData.searchedData) {
        if (this.props.Login.masterData.searchedData.length <= this.state.skip) {
          this.setState({
            skip: 0
          });
        }
      }
    }

    if (bool) {
      this.setState({
        selectedRecord,
        comboitem,
        filterData,
        selectedcombo,
        filterCatList,
        selectedMaterialCat,
        selectedMaterialcombo,
        materialCatList,
        MaterialComboList,
        SelectedMaterialInventory
      });
    }
  }

  componentWillUnmount() {
    const updateInfo = {
      typeName: DEFAULT_RETURN,
      data: {
        masterData: [],
        inputParam: undefined,
        operation: null,
        modalName: undefined
      }
    };
    this.props.updateStore(updateInfo);
  }

  handleClickDelete(masterData, ncontrolcode, modalName) {
    if (this.props.Login.masterData["SelectedMaterialInventory"].jsondata.ntranscode !== transactionStatus.RETIRED && this.props.Login.masterData["SelectedMaterialInventory"].jsondata.ntranscode !== transactionStatus.RELEASED) {
      const fieldArray = [];
      let postParam = {
        inputListName: "MaterialInventory",
        selectedObject: "SelectedMaterialInventory",
        primaryKeyField: "nmaterialinventorycode",
        primaryKeyValue: fieldArray,
        fetchUrl: "materialinventory/getMaterialInventory",
        fecthInputObject: {
          userinfo: this.props.Login.userInfo
        },
        "nmaterialtypecode": this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode,
        "nmaterialcatcode": this.state.selectedMaterialCat["nmaterialcatcode"].value,
        "nmaterialcode": this.props.Login.masterData.SelectedMaterialCrumb['jsondata'].nmaterialcode
      };
      modalName = 'openModal';
      const inputParam = {
        methodUrl: "MaterialInventory",
        classUrl: "materialinventory",
        inputData: {
          "material": this.props.Login.masterData.SelectedMaterial,
          "nmaterialcode": this.props.Login.masterData.SelectedMaterialCrumb['jsondata'].nmaterialcode,
          "nmaterialinventorycode": this.props.Login.masterData.SelectedMaterialInventory.jsondata.nmaterialinventorycode,
          "nmaterialcatcode": this.state.selectedMaterialCat["nmaterialcatcode"].value,
          "userinfo": this.props.Login.userInfo,
          "nmaterialtypecode": this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode
        },
        operation: "delete",
        postParam,
        displayName: "Material"
      };

      if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, ncontrolcode)) {
        const updateInfo = {
          typeName: DEFAULT_RETURN,
          data: {
            loadEsign: true,
            screenData: {
              inputParam,
              masterData
            },
            [modalName]: true,
            operation: 'delete',
            screenName: "Material",
            id: "material"
          }
        };
        this.props.updateStore(updateInfo);
      } else {
        this.props.crudMaster(inputParam, masterData, modalName);
      }
    } else {
      toast.warn(this.props.intl.formatMessage({
        id: "IDS_SELECTQUARENTINEINVENTORY"
      }));
    }
  }

}

export default connect(mapStateToProps, {
  callService,
  getAddMaterialInventoryPopup,
  updateMaterialStatus,
  openDatePopup,
  getQuantityTransactionPopup,
  getQuantityTransactionOnchange,
  updateStore,
  initialcombochangeMaterialInvget,
  viewAttachment,
  getMaterialChildValues,
  crudMaster,
  getMaterialInventoryDetails,
  getMaterialInventoryByID,
  addMaterialInventoryFile,
  filterColumnData,
  validateEsignCredential
})(injectIntl(MaterialInventory));

