import React, { Component } from "react";
import merge from "lodash/merge";
import {
  Query,
  Builder,
  BasicConfig, BasicFuncs,
  Utils as QbUtils,
} from "react-awesome-query-builder";
import "react-awesome-query-builder/lib/css/styles.css";
import { FormattedMessage, injectIntl } from "react-intl";
import AddFilteredPatient from "../pages/contactmaster/AddFilteredPatient";
import { Pager } from "@progress/kendo-react-data-tools";
import { Grid, GridColumn } from "@progress/kendo-react-grid";
import { Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalculator } from "@fortawesome/free-solid-svg-icons";

const InitialConfig = BasicConfig;

const conjunctions = {
  AND: InitialConfig.conjunctions.AND,
  OR: InitialConfig.conjunctions.OR,
};
const operators1 = {
  ...InitialConfig.operators
}
delete operators1.proximity
const operators = {
  ...operators1,
  between: {
    ...operators1.between,
    textSeparators: [
      "from",
      "to"
    ],
  }
};


const widgets = {
  ...InitialConfig.widgets,
  date: {
    ...InitialConfig.widgets.date,
    dateFormat: "DD.MM.YYYY",
    valueFormat: "YYYY-MM-DD",
  }
};


const types = {
  ...InitialConfig.types,
  boolean: merge(InitialConfig.types.boolean, {
    widgets: {
      boolean: {
        widgetProps: {
          hideOperator: true,
          operatorInlineLabel: "is",
        }
      },
    },
  }),
};


const localeSettings = {
  locale: {
    moment: "ru"

  },
  valueLabel: "Value",
  valuePlaceholder: "Value",
  operatorLabel: "Operator",
  fieldPlaceholder: "Select field",
  operatorPlaceholder: "Select operator",
  deleteLabel: null,
  addGroupLabel: "Add group",
  addRuleLabel: "Add rule",
  addSubRuleLabel: "Add sub rule",
  delGroupLabel: null,
  notLabel: "Not",
  valueSourcesPopupTitle: "Select value source",
  removeRuleConfirmOptions: {
    title: "Are you sure delete this rule?",
    okText: "Yes",
    okType: "danger",
  },
  removeGroupConfirmOptions: {
    title: "Are you sure delete this group?",
    okText: "Yes",
    okType: "danger",
  },
};
const settings1 = { ...InitialConfig.settings }
delete settings1.field
const settings = {
  ...settings1,
  ...localeSettings,

  valueSourcesInfo: {
    value: {
      label: "Value"
    }
    ,
    field: {
      label: "Field",
      widget: "field",
    },
    func: {
      label: "Function",
      widget: "func",
    }
  },
  // canReorder: false,
  // canRegroup: false,
  // showNot: false,
  // showLabels: true,
  maxNesting: 5,
  canLeaveEmptyGroup: true,
  shouldCreateEmptyGroup: false,
  showErrorMessage: true,
  customFieldSelectProps: {
    showSearch: true
  }, //after deletion

  // renderField: (props) => <FieldCascader {...props} />,
  // renderOperator: (props) => <FieldDropdown {...props} />,
  // renderFunc: (props) => <FieldSelect {...props} />,
};

const funcs = {
  ...BasicFuncs
};
// delete funcs.valueSourcesInfo.field
const config = {
  ...InitialConfig, conjunctions,
  operators,
  widgets,
  types,
  settings,
  funcs,
  fields: {}

};
class FilterQueryBuilder extends Component {
  constructor(props) {

    super(props);
    this.state = {
      config: {
        ...config, fields: this.props.fields
      }
    }
  }



  render = () => (

    <React.Fragment>
      {!this.props.static ?
        <Button className="btn-user btn-primary-blue" style={{ "margin-left": '900px' }} onClick={() => this.props.handleExecuteClick()}>
          <FontAwesomeIcon icon={faCalculator} /> { }
          <FormattedMessage id='IDS_EXECUTE' defaultMessage='Execute' />
        </Button>
        : ""}
      <Query
        {...(this.props.config || this.state.config)}
        value={this.props.tree || this.state.tree}
        onChange={this.props.onChange}
        renderBuilder={this.renderBuilder}

      />
      {!this.props.static ?
        <hr /> : ""}
      {!this.props.static ?
        <Grid
          style={{
            maxHeight: "400px",
          }}
          onRowClick={this.props.onRowClick}
          data={this.props.filterData.slice(this.props.skip, this.props.skip + this.props.take)}
        >
          {this.props.gridColumns.map(x => {

            return <GridColumn field={x.field} title={x.title} width={x.width} />

          })}

          {/* <GridColumn field="UnitPrice" title="Price" />
              <GridColumn field="Discontinued" title="Discontinued" /> */}
        </Grid>
        : ""}
      {!this.props.static ?
        <hr />
        : ""}
      {!this.props.static ?
        <Pager
          skip={this.props.skip}
          take={this.props.take}
          type="input"
          previousNext={true}
          total={this.props.filterData.length}
          onPageChange={this.props.handlePageChange}
        />
        : ""}
      {/* {this.props.slideResult && this.props.slideResult.length>0?
      // <AddFilteredPatient
      //           slideList={this.props.slideList}
      //           slideResult={this.props.slideResult}
      //           controlMap={this.props.controlMap}
      //           dataState={this.props.dataState}
      //           dataStateChange={this.props.dataStateChange}
      //         />

    :""} */}
    </React.Fragment>

  );

  renderBuilder = (props) => (
    <div className="query-builder-container" style={{ padding: "25px", width: "1000px" }}>
      <div className="query-builder qb-lite">
        <Builder {...props}
        />
      </div>
    </div>
  );
  // onChange = (immutableTree, config) => {
  //   this.setState({ tree: immutableTree, config: config });

  //   const jsonTree = QbUtils.getTree(immutableTree);
  //   console.log(jsonTree);
  // };
}
export default injectIntl(FilterQueryBuilder);
