const mapStateToProps = state => {
    return ({ Login: state.Login })
}
class TrainingUpdateChildTab extends React.Component {

    constructor(props) {
        super(props);
        const dataState = {
            skip: 0,
            take: this.props.settings? parseInt(this.props.settings[14]) : 5
        };
        const dataStateParticipants = {
            skip: 0,
            take:this.props.settings? parseInt(this.props.settings[14]) : 5
        };
        this.state = {
