import React from 'react';
import { ListWrapper } from '../../components/client-group.styles';
import { Col, Row, Card, Nav, FormGroup, FormLabel } from 'react-bootstrap';
import ListMaster from '../../components/list-master/list-master.component';
import { connect } from 'react-redux';
import { injectIntl } from 'react-intl';
import { FormattedMessage } from "react-intl";
import { ReadOnlyText, ContentPanel } from "../../components/App.styles";
import {
    getControlMap, onSaveMandatoryValidation, showEsign, Lims_JSON_stringify, create_UUID, replaceUpdatedObject,
    rearrangeDateFormat
} from '../../components/CommonScript'
import { toast } from 'react-toastify';
import {
    callService, getOOSMasterData, getOosMasterDetail, onSaveOOSWorkFlowTransaction, updateStore, viewAttachment,
    // onSaveOOSMasterChecklist,
    onSaveOOSChecklistQb, deleteOOSWorkFlowTransaction, getOOSDetailsData, filterColumnData, onUpdateOOSWorkFlowTransaction
} from '../../actions';
import { transactionStatus, attachmentType } from '../../components/Enumeration';
import DataGrid from '../../components/data-grid/data-grid.component';
import { process } from '@progress/kendo-data-query';
import Axios from 'axios';
import { DEFAULT_RETURN } from '../../actions/LoginTypes';
import rsapi from '../../rsapi';
import AddOOSWorkFlowTransaction from './AddOOSWorkFlowTransaction';
import SlideOutModal from '../../components/slide-out-modal/SlideOutModal';
import EsignStateHandle from '../audittrail/EsignStateHandle';
import { constructOptionList } from '../../components/CommonScript';
// import AddOOSMasterChecklist from './AddOOSMasterChecklist';
import AddOOSChecklistQb from './AddOOSChecklistQb';
import { ReactComponent as Close } from '../../assets/image/rectangle-xmark-regular.svg';
import { ReactComponent as Open } from '../../assets/image/open.svg';
import { faEye } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const mapStateToProps = state => { return ({ Login: state.Login }) }

class OOSMaster extends React.Component {
    constructor(props) {
        super(props);
        this.searchRef = React.createRef();
        this.state = {
            dataState: { skip: 0, take: this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5 },
            oosDetailsDataState: { skip: 0, take: this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5 },
            userRoleControlRights: [],
            controlMap: new Map(),
            masterStatus: "",
            error: "",
            selectedRecord: {},
            operation: "",
            skip: 0,
            take: this.props.Login.settings && this.props.Login.settings[3],
            data: []
        }
    }

    render() {

        const filterParam = {
            inputListName: "OosMaster", selectedObject: "selectedOosMaster", primaryKeyField: "noosmastercode",
            fetchUrl: "oos/getActiveOosMasterById", fecthInputObject: { userinfo: this.props.Login.userInfo },
            masterData: this.props.Login.masterData,
            searchFieldList: ["sarno", "soosno", "sproductname", "ssamplearno", "stransdisplaystatus", "sopendate", "sclosedate", "stestname"]
        };

        const editOOSWorkFlowTransactionId =
            this.state.controlMap.has("EditWorkFlowTransaction") &&
            this.state.controlMap.get("EditWorkFlowTransaction").ncontrolcode;

        const viewOOSWorkFlowTransactionId =
            this.state.controlMap.has("ViewWorkFlowTransaction") &&
            this.state.controlMap.get("ViewWorkFlowTransaction").ncontrolcode;

        const viewParamWorkFlowTransaction = {
            screenName: this.props.Login.screenName, primaryKeyField: "noosworkflowtransactioncode", operation: "view",
            inputParam: this.props.Login.inputParam, userInfo: this.props.Login.userInfo, ncontrolCode: viewOOSWorkFlowTransactionId, masterData: this.props.Login.masterData
        };

        const editParamWorkFlowTransaction = {
            screenName: this.props.Login.screenName, primaryKeyField: "noosworkflowtransactioncode", operation: "update",
            inputParam: this.props.Login.inputParam, userInfo: this.props.Login.userInfo, ncontrolCode: editOOSWorkFlowTransactionId, masterData: this.props.Login.masterData
        };

        const openOOSId =
            this.state.controlMap.has("OpenOOS") &&
            this.state.controlMap.get("OpenOOS").ncontrolcode;

        const closeOOSId =
            this.state.controlMap.has("CloseOOS") &&
            this.state.controlMap.get("CloseOOS").ncontrolcode;

        const viewOOSDetailsId =
            this.state.controlMap.has("ViewOOSDetails") &&
            this.state.controlMap.get("ViewOOSDetails").ncontrolcode;

        const openOOSParam = {
            operation: "open", primaryKeyField: "noosmastercode",
            userInfo: this.props.Login.userInfo, ncontrolCode: openOOSId, masterData: this.props.Login.masterData
        }

        const closeOOSParam = {
            operation: "close", primaryKeyField: "noosmastercode",
            userInfo: this.props.Login.userInfo, ncontrolCode: closeOOSId, masterData: this.props.Login.masterData
        }

        const oosDetailParam = {
            operation: "view", primaryKeyField: "noosmastercode",
            userInfo: this.props.Login.userInfo, ncontrolCode: closeOOSId, masterData: this.props.Login.masterData
        }

        let userStatusCSS = "";
        let activeIconCSS = "fa fa-check";
        if (this.props.Login.masterData.selectedOosMaster && this.props.Login.masterData.selectedOosMaster.ntransactionstatus === transactionStatus.DRAFT) {
            userStatusCSS = "outline-secondary";
            activeIconCSS = "";
        }
        else if (this.props.Login.masterData.selectedOosMaster && this.props.Login.masterData.selectedOosMaster.ntransactionstatus === transactionStatus.RECEIVED) {
            userStatusCSS = "outline-success";
        }
        else {
            userStatusCSS = "outline-Final";
        }

        return (
            <>
                <ListWrapper className="client-listing-wrap mtop-fixed-breadcrumb fixed_breadcrumd">
                    <Row noGutters={true} >
                        <Col md={`${!this.props.sidebarview ? '4' : "2"}`}>
                            <ListMaster
                                formatMessage={this.props.intl.formatMessage}
                                screenName={"IDS_OOSMASTER"}
                                masterData={this.props.Login.masterData}
                                userInfo={this.props.Login.userInfo}
                                masterList={this.props.Login.masterData.searchedData || this.props.Login.masterData.OosMaster}
                                getMasterDetail={(OosMaster) => this.props.getOosMasterDetail(OosMaster, this.props.Login.userInfo, this.props.Login.masterData)}
                                selectedMaster={this.props.Login.masterData.selectedOosMaster}
                                primaryKeyField="noosmastercode"
                                mainField="soosno"
                                firstField="stestname"
                                secondField="sarno"
                                isIDSField="Yes"
                                filterColumnData={this.props.filterColumnData}
                                filterParam={filterParam}
                                userRoleControlRights={this.state.userRoleControlRights}
                                addId={undefined}
                                searchRef={this.searchRef}
                                reloadData={this.reloadData}
                                hidePaging={false}
                                openModal={undefined}
                                showFilterIcon={false}
                                showFilter={false}
                                filterComponent={[]}
                            />
                        </Col>
                        <Col md={`${!this.props.sidebarview ? '8' : "10"}`} className="position-relative">
                            <div className="sidebar-view-btn-block">
                                <div className="sidebar-view-btn " onClick={this.props.parentFunction}>
                                    {!this.props.sidebarview ?
                                        <i class="fa fa-less-than"></i> :
                                        <i class="fa fa-greater-than"></i>
                                    }
                                </div>
                            </div>
                            <Row>
                                <Col md={12}>
                                    <ContentPanel className="panel-main-content">
                                        {this.props.Login.masterData.selectedOosMaster && this.props.Login.masterData.OosMaster && this.props.Login.masterData.OosMaster.length > 0 && this.props.Login.masterData.selectedOosMaster ?
                                            <Card className="border-0">
                                                <Card.Header>
                                                    <Card.Title className="product-title-main">{this.props.Login.masterData.selectedOosMaster.stestname}</Card.Title>
                                                    <Card.Subtitle>
                                                        <div className="d-flex product-category">
                                                            <h2 className="product-title-sub flex-grow-1">
                                                                <span className={`btn btn-outlined ${userStatusCSS} btn-sm`}>
                                                                    <i className={activeIconCSS}></i>
                                                                    {this.props.Login.masterData.selectedOosMaster.stransdisplaystatus}
                                                                </span>
                                                            </h2>
                                                            <div className="d-inline ">
                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    hidden={this.state.userRoleControlRights.indexOf(openOOSId) === -1}
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_GENERATEOOS" })}
                                                                    onClick={(e) => this.openCloseOOS(openOOSParam)} >
                                                                    <Open className="custom_icons" width="15" height="15" />
                                                                </Nav.Link>
                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    hidden={this.state.userRoleControlRights.indexOf(closeOOSId) === -1}
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_CLOSE" })}
                                                                    onClick={(e) => this.openCloseOOS(closeOOSParam)} >
                                                                    <Close className="custom_icons" width="15" height="15" />
                                                                </Nav.Link>
                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    hidden={this.state.userRoleControlRights.indexOf(viewOOSDetailsId) === -1}
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_OOSDETAILS" })}
                                                                    onClick={(e) => this.getOOSDetails(oosDetailParam)} >
                                                                    <FontAwesomeIcon icon={faEye} />
                                                                </Nav.Link>
                                                            </div>
                                                        </div>
                                                    </Card.Subtitle>
                                                </Card.Header>
                                                <Card.Body>
                                                    {/* <CustomTab tabDetail={this.tabDetail()} onTabChange={this.onTabChange} /> */}
                                                    <DataGrid
                                                        primaryKeyField={"noosmastercode"}
                                                        data={this.props.Login.masterData.OosWorkFlowTransaction || []}
                                                        dataResult={process(this.props.Login.masterData.OosWorkFlowTransaction || [], this.state.dataState)}
                                                        dataState={this.state.dataState}
                                                        dataStateChange={this.dataStateChange}
                                                        extractedColumnList={[
                                                            { "idsName": "IDS_STEPNAME", "dataField": "soosworkflowtypename", "width": "200px" },
                                                            { "idsName": "IDS_COMMENTS", "dataField": "scomments", "width": "200px" },
                                                            { "idsName": "IDS_ADDITIONALCOMMENTS", "dataField": "sadditionalcomments", "width": "200px" },
                                                            { "idsName": "IDS_STATUS", "dataField": "stransdisplaystatus", "width": "200px" },
                                                            { "idsName": "IDS_FILENAME", "dataField": "sfilename", "width": "200px" }
                                                        ]}
                                                        userInfo={this.props.Login.userInfo}
                                                        controlMap={this.state.controlMap}
                                                        userRoleControlRights={this.state.userRoleControlRights}
                                                        inputParam={this.props.Login.inputParam}
                                                        deleteRecord={this.deleteOOSWorkFlowTransaction}
                                                        methodUrl={"WorkFlowTransaction"}
                                                        pageable={true}
                                                        isComponent={false}
                                                        hasDynamicColSize={true}
                                                        isActionRequired={true}
                                                        isToolBarRequired={true}
                                                        isAddRequired={true}
                                                        addRecord={this.openOosMasterStep}
                                                        editParam={editParamWorkFlowTransaction}
                                                        fetchRecord={this.editOosMasterStep}
                                                        isRefreshRequired={false}
                                                        isImportRequired={false}
                                                        isDownloadPDFRequired={false}
                                                        isDownloadExcelRequired={false}
                                                        scrollable={'scrollable'}
                                                        gridHeight={'600px'}
                                                        expandField="expanded"
                                                        viewParam={viewParamWorkFlowTransaction}
                                                        viewRecord={this.viewChecklistQb}
                                                        viewDownloadFile={this.downloadWorkFlowTransactionFile}
                                                    />
                                                </Card.Body>
                                            </Card>
                                            : ""}
                                    </ContentPanel>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </ListWrapper>
                {this.props.Login.openModal ?
                    <SlideOutModal
                        show={this.props.Login.openModal}
                        size={this.props.Login.screenName === "IDS_OOSDETAILS" ? "xl" : "lg"}
                        closeModal={this.closeModal}
                        operation={this.props.Login.operation}
                        inputParam={this.props.Login.inputParam}
                        screenName={this.props.Login.screenName}
                        onSaveClick={this.onMandatoryCheck}
                        esign={this.props.Login.loadEsignStateHandle}
                        validateEsign={this.validateEsign}
                        updateStore={this.props.updateStore}
                        selectedRecord={this.state.selectedRecord || {}}
                        mandatoryFields={[]}
                        hideSave={this.props.Login.screenName === "IDS_OOSDETAILS" ? true :
                            (this.props.Login.screenName === "IDS_OOSCHECKLISTQB" && this.props.Login.masterData
                                && this.props.Login.masterData.selectedOosMaster
                                && this.props.Login.masterData.selectedOosMaster.ntransactionstatus === transactionStatus.CLOSED) ? true : false}
                        addComponent={this.props.Login.loadEsignStateHandle ? (
                            <EsignStateHandle
                                operation={this.props.Login.operation}
                                inputParam={this.props.Login.inputParam}
