    onSaveClick = (saveType, formRef) => {
        if (this.props.Login.screenName === "IDS_OOSWORKFLOWSTEP") {
            if (this.props.Login.operation === "create") {
                const formData = new FormData();
                const selectedRecord = this.state.selectedRecord;
                const acceptedFiles = selectedRecord.sfilename;
                const userInfo = this.props.Login.userInfo;
                const masterData = this.props.Login.masterData;
                let isFileEdited = transactionStatus.NO;
                let fileArray = [];
                if (acceptedFiles && Array.isArray(acceptedFiles) && acceptedFiles.length > 0) {
                    if (this.props.Login.operation === 'create') {
                        acceptedFiles.forEach((file, index) => {
                            const splittedFileName = file.name.split('.');
                            const fileExtension = file.name.split('.')[splittedFileName.length - 1];
                            const fileName = selectedRecord.ssystemfilename && selectedRecord.ssystemfilename !== "" ?
                                selectedRecord.ssystemfilename.split('.')[0] : create_UUID();
                            const uniquefilename = fileName + '.' + fileExtension;
                            const tempData = {};
                            tempData["noosmastercode"] = masterData.selectedOosMaster && masterData.selectedOosMaster.noosmastercode ? masterData.selectedOosMaster.noosmastercode : -1;
                            tempData["noosworkflowtypecode"] = selectedRecord.noosworkflowtypecode.value;
                            tempData["ntranscode"] = selectedRecord.ntranscode.value;
                            tempData["scomments"] = selectedRecord.scomments;
                            tempData["sadditionalcomments"] = selectedRecord.sadditionalcomments;
                            tempData["sfilename"] = file.name ? Lims_JSON_stringify(file.name.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false) : null;
                            tempData["ssystemfilename"] = uniquefilename;
                            tempData["nfilesize"] = file.size;
                            formData.append("uploadedFile" + index, file);
                            formData.append("uniquefilename" + index, uniquefilename);
                            fileArray.push(tempData);
                        });
                        formData.append("filecount", acceptedFiles.length);
                        isFileEdited = transactionStatus.YES;
                    } else {
                        acceptedFiles.forEach((file, index) => {
                            const splittedFileName = file.name.split('.');
                            const fileExtension = file.name.split('.')[splittedFileName.length - 1];
                            const fileName = selectedRecord.ssystemfilename && selectedRecord.ssystemfilename !== "" ?
                                selectedRecord.ssystemfilename.split('.')[0] : create_UUID();
                            const uniquefilename = fileName + '.' + fileExtension;
                            const tempData = {};
                            tempData["noosmastercode"] = masterData.selectedOosMaster && masterData.selectedOosMaster.noosmastercode ? masterData.selectedOosMaster.noosmastercode : -1;
                            tempData["noosworkflowtypecode"] = selectedRecord.noosworkflowtypecode.value;
                            tempData["ntranscode"] = selectedRecord.ntranscode.value;
                            tempData["scomments"] = selectedRecord.scomments;
                            tempData["sadditionalcomments"] = selectedRecord.sadditionalcomments;
                            tempData["sfilename"] = file.name ? Lims_JSON_stringify(file.name.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false) : null;
                            tempData["ssystemfilename"] = uniquefilename;
                            tempData["nfilesize"] = file.size;
                            formData.append("uploadedFile" + index, file);
                            formData.append("uniquefilename" + index, uniquefilename);
                            formData.append("filecount", acceptedFiles.length);
                            isFileEdited = transactionStatus.YES;
                            fileArray.push(tempData);
                        });
                    }
                } else {
                    let tempData = {};
                    tempData["noosmastercode"] = masterData.selectedOosMaster && masterData.selectedOosMaster.noosmastercode ? masterData.selectedOosMaster.noosmastercode : -1;
                    tempData["noosworkflowtypecode"] = selectedRecord.noosworkflowtypecode.value;
                    tempData["ntranscode"] = selectedRecord.ntranscode.value;
                    tempData["scomments"] = selectedRecord.scomments;
                    tempData["sadditionalcomments"] = selectedRecord.sadditionalcomments;
                    tempData["sfilename"] = acceptedFiles ? Lims_JSON_stringify(acceptedFiles.replaceAll('\\', '\\\\\\\\').replaceAll('\n', '\\\\n').replaceAll('"', '\\"').trim(), false) : null;
                    tempData["ssystemfilename"] = selectedRecord.ssystemfilename;
                    tempData["nfilesize"] = selectedRecord.size;
                    fileArray.push(tempData);
                }
                formData.append("isFileEdited", isFileEdited);
                formData.append("oosworkflowtransaction", JSON.stringify(fileArray));
                formData.append("nattachmenttypecode", attachmentType.FTP);
                formData.append("noosworkflowtransactioncode", masterData.selectedOosMaster);
                formData.append("ncontrolcode", this.props.Login.ncontrolCode);
                formData.append("userinfo", JSON.stringify({
                    ...userInfo,
                    sformname: Lims_JSON_stringify(userInfo.sformname),
                    smodulename: Lims_JSON_stringify(userInfo.smodulename),
                    slanguagename: Lims_JSON_stringify(userInfo.slanguagename)
                }));

                const inputParam = {
                    inputData: {
                        "userinfo": {
                            ...userInfo,
                            sformname: Lims_JSON_stringify(userInfo.sformname),
                            smodulename: Lims_JSON_stringify(userInfo.smodulename),
                            slanguagename: Lims_JSON_stringify(userInfo.slanguagename)
                        },
                        formData: formData,
                        isFileupload: true,
                        operation: this.props.Login.operation,
                        classUrl: "oos",
                        saveType: saveType,
                        formRef: formRef,
                        methodUrl: "OOSWorkFlowTransaction",
                        selectedRecord: this.state.selectedRecord,
                        screenName: this.props.Login.screenName,
                        masterData: this.props.Login.masterData
                    }
                };

                if (showEsign(this.props.Login.userRoleControlRights, userInfo.nformcode, this.props.Login.ncontrolCode)) {
                    const updateInfo = {
                        typeName: DEFAULT_RETURN,
                        data: {
                            screenData: { inputParam },
                            loadEsignStateHandle: true,
                            ncontrolcode: this.props.Login.ncontrolCode
                        }
                    }
                    this.props.updateStore(updateInfo);
                } else {
                    this.props.onSaveOOSWorkFlowTransaction(inputParam);
                }
            } else {
                const selectedRecord = this.state.selectedRecord;
                const userInfo = this.props.Login.userInfo;
                const masterData = this.props.Login.masterData;
                let inputData = {
                    userinfo: this.props.Login.userInfo,
                    noosmastercode: masterData.selectedOosMaster && masterData.selectedOosMaster.noosmastercode ? masterData.selectedOosMaster.noosmastercode : -1,
                    noosworkflowtransactioncode: this.props.Login.selectedId,
                    ntranscode: selectedRecord.ntranscode.value,
                    scomments: selectedRecord.scomments,
                    sadditionalcomments: selectedRecord.sadditionalcomments
                }

                let inputParam = {
                    masterData,
                    inputData,
                    operation: this.props.Login.operation,
                    classUrl: "oos",
                    methodUrl: "OOSWorkFlowTransaction"
                }

                if (showEsign(this.props.Login.userRoleControlRights, userInfo.nformcode, this.props.Login.ncontrolCode)) {
                    const updateInfo = {
                        typeName: DEFAULT_RETURN,
                        data: {
                            screenData: { inputParam },
                            loadEsignStateHandle: true,
                            ncontrolcode: this.props.Login.ncontrolCode
                        }
                    }
                    this.props.updateStore(updateInfo);
                } else {
                    this.props.onUpdateOOSWorkFlowTransaction(inputParam);
                }

            }
        } else if (this.props.Login.screenName === "IDS_OOSCHECKLISTQB") {
            const failedControls = [];
            const mandatoryFields = [];
            this.props.Login.masterData.templateData.map(control =>
                control.nmandatoryfield === transactionStatus.YES ? mandatoryFields.push(control) : ''
            )
            mandatoryFields.forEach(item => {
                if (this.state.selectedRecord && this.state.selectedRecord[item.nchecklistversionqbcode] && this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue) {
                    if (typeof this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue === "object") {
                        if (this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue.length === 0) {
                            const alertMessage = item.squestion
                            failedControls.push(alertMessage);
                        }
                    }
                    else if (typeof this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue === "string") {
                        if (this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue.trim().length === 0) {
                            const alertMessage = item.squestion
                            failedControls.push(alertMessage);
                        }
                    }
                    else {
                        if (this.state.selectedRecord[item.nchecklistversionqbcode].sdefaultvalue.length === 0) {
                            const alertMessage = item.squestion
                            failedControls.push(alertMessage);
                        }
                    }
                }
                else {
                    const alertMessage = item.squestion
                    failedControls.push(alertMessage);
                }
            });
            if (failedControls.length === 0) {
                let inputData = [];
                inputData["userinfo"] = this.props.Login.userInfo;
                inputData["noosworkflowtransactioncode"] = this.props.Login.selectedId;
                const inputParam = {
                    classUrl: this.props.Login.inputParam.classUrl,
                    methodUrl: "OOSChecklistQb",
                    displayName: this.props.Login.inputParam.displayName,
                    inputData: inputData,
                    operation: this.props.Login.operation,
                    selectedRecord: this.state.selectedRecord,
                    masterData: this.props.Login.masterData
                }
                // if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, this.props.Login.ncontrolCode)) {
                //     const updateInfo = {
                //         typeName: DEFAULT_RETURN,
