                //         }
                //     }
                //     this.props.updateStore(updateInfo);
                // }
                // else {
                this.props.onSaveOOSChecklistQb(inputParam);
                // }
            }
            else {
                toast.info(`${this.props.intl.formatMessage({ id: "IDS_ENTER" })} ${failedControls[0]}`);
            }
        }
    }

    openCloseOOS = (parameter) => {
        let masterData = parameter.masterData;
        let urlArray = [];
        const url = "oos/openCloseOOS";
        urlArray.push(rsapi.post(url, {
            userinfo: this.props.Login.userInfo,
            noosmastercode: masterData.selectedOosMaster && masterData.selectedOosMaster.noosmastercode
                && masterData.selectedOosMaster.noosmastercode,
            operation: parameter.operation
        }))
        Axios.all(urlArray)
            .then(response => {
                let OosMaster = masterData.OosMaster;
                if (response[0].data && response[0].data.selectedOosMaster && response[0].data.selectedOosMaster != null) {
                    OosMaster = replaceUpdatedObject([response[0].data.selectedOosMaster], OosMaster, "noosmastercode");
                }
                masterData = {
                    ...masterData,
                    ...response[0].data,
                    ...OosMaster
                }
                const updateInfo = {
                    typeName: DEFAULT_RETURN,
                    data: {
                        masterData,
                    }
                }
                this.props.updateStore(updateInfo);
            })
            .catch(error => {
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
                this.setState({
                    loading: false
                });
            });
    }

    getOOSDetails = (oosDetailParam) => {
        const masterData = oosDetailParam.masterData;
        let inputData = {};
        inputData["noosmastercode"] = masterData && masterData.selectedOosMaster ? masterData.selectedOosMaster.noosmastercode : -1;
        inputData["userinfo"] = this.props.Login.userInfo;
        const inputParam = {
            classUrl: this.props.Login.inputParam.classUrl,
            methodUrl: "OOSDetails",
            inputData: inputData,
            operation: "view",
            selectedRecord: this.state.selectedRecord,
            masterData: masterData,
            screenName: "IDS_OOSDETAILS"
        }
        this.props.getOOSDetailsData(inputParam);
    }

