import React from 'react';
import { injectIntl } from 'react-intl';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { Col, Row } from 'react-bootstrap';
import FormInput from '../../components/form-input/form-input.component';
import CustomSwitch from '../../components/custom-switch/custom-switch.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';
import FormNumericInput from '../../components/form-numeric-input/form-numeric-input.component';
import AddTestMethod from './AddTestMethod';
import AddTestInstrumentCategory from './AddTestInstrumentCategory';
import AddTestSection from './AddTestSection';
import AddTestPackageTest from './AddTestPackageTest';

const AddTest = (props) => {
    const {period} = props.parameterData;
    return (
        <Row>
              <Col md={12}>
            
                <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_TESTCATEGORY" })}
                    isSearchable={true}
                    name={"ntestcategorycode"}
                    placeholder={props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                    isMandatory={true}
                    showOption={true}
                    options={props.testData.testCategory || []}
                    optionId='ntestcategorycode'
                    optionValue='stestcategoryname'
                    value={props.selectedRecord["ntestcategorycode"]}
                    onChange={value => props.onComboChange(value, "ntestcategorycode", 1)}
                    alphabeticalSort={true}
                >
                </FormSelectSearch>
                <FormInput
                    name={"stestname"}
                    label={props.intl.formatMessage({ id: "IDS_TESTNAME" })}
                    type="text"
                    onChange={(event) => props.onInputOnChange(event, 2)}
                    placeholder={props.intl.formatMessage({ id: "IDS_TESTNAME" })}
                    value={props.selectedRecord?props.selectedRecord["stestname"]:""}
                    isMandatory="*"
                    required={true}
                    maxLength={100}
                />
                <FormInput
                    name={"stestsynonym"}
                    label={props.intl.formatMessage({ id: "IDS_TESTSYNONYM" })}
                    type="text"
                    onChange={(event) => props.onInputOnChange(event, 1)}
                    placeholder={props.intl.formatMessage({ id: "IDS_TESTSYNONYM" })}
                    value={props.selectedRecord?props.selectedRecord["stestsynonym"]:""}
                    isMandatory="*"
                    required={true}
                    maxLength={100}
                />
   { props.needOtherTest && 
            <AddTestSection
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                section={props.otherTestData.section}
                isMulti={false}
            />}
            { props.needOtherTest && 
            <AddTestMethod
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                method={props.otherTestData.method}
                isMulti={false}
            />}

{ props.needOtherTest && 
            <AddTestInstrumentCategory
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                instrumentcategory={props.otherTestData.instrumentcategory}
                isMulti={false}
            />}
                <FormNumericInput
                    name={"ncost"}
                    label={props.intl.formatMessage({ id: "IDS_PRICE" })}
                    type="number"
                    value={props.selectedRecord["ncost"]}
                    placeholder={props.intl.formatMessage({ id: "IDS_PRICE" })}
                    strict={true}
                    min={0}
                    //max={9999999.99}
                    maxLength={10}
                    onChange={(value) => props.onNumericInputChange(value, "ncost")}
                    noStyle={true}
                    precision={2}
                    className="form-control"
                    errors="Please provide a valid number."
                />

                <FormTextarea
                    name={"sdescription"}
                    label={props.intl.formatMessage({ id: "IDS_TESTPROCEDURE" })}
                    onChange={(event) => props.onInputOnChange(event, 1)}
                    placeholder={props.intl.formatMessage({ id: "IDS_TESTPROCEDURE" })}
                    value={props.selectedRecord?props.selectedRecord["sdescription"]:""}
                    rows="2"
                    required={false}
                    maxLength={255}
                >
                </FormTextarea>


      



{/* 
            </Col>
            <Col md={8}> */}

<Row>
              <Col md={6}>
            <CustomSwitch
                    name={"naccredited"}
                    label={props.intl.formatMessage({ id: "IDS_ACCREDITED" })}
                    type="switch"
                    onChange={(event) => props.onInputOnChange(event, 1, [70, 71])}
                    placeholder={props.intl.formatMessage({ id: "IDS_ACCREDITED" })}
                    defaultValue={props.selectedRecord["naccredited"] === 70 ? true : false }
                    checked={props.selectedRecord["naccredited"] === 70 ? true : false }
                >
                </CustomSwitch>
                </Col>
                <Col md={6}>
                <CustomSwitch
                    name={"ntransactionstatus"}
                    label={props.intl.formatMessage({ id: "IDS_ACTIVE"})}
                    type="switch"
                    onChange={(event)=>props.onInputOnChange(event, 1, [1, 2])}
                    //onChange={(event)=>props.onActiveStatusChange(event, [1, 2])}
                    placeholder={props.intl.formatMessage({ id: "IDS_ACTIVE"})}
                    defaultValue ={props.selectedRecord["ntransactionstatus"] === 1 ? true :false}
                    checked={props.selectedRecord["ntransactionstatus"] === 1 ? true :false}
                >
                </CustomSwitch>
                </Col>
                </Row>
                { props.needOtherTest && 
            <AddTestPackageTest
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                package={props.otherTestData.package}
                isMulti={false}
            />}
 
 <Row>
              <Col md={6}>
                <FormNumericInput
                    name={"ntat"}
                    label={props.intl.formatMessage({ id: "IDS_TAT" })}
                    type="number"
                    value={props.selectedRecord["ntat"]}
                    placeholder={props.intl.formatMessage({ id: "IDS_TAT" })}
                    strict={true}
                    min={0}
                    //max={9999999.99}
                    maxLength={3}
                    onChange={(value) => props.onNumericInputChange(value, "ntat")}
                    noStyle={true}
                    className="form-control"
                    errors="Please provide a valid number."
                />
                 </Col>
                 <Col md={6}>
                 <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_TATUNIT" })}
                    isSearchable={true}
                    name={"ntatunitcode"}
                    //isDisabled={needUnit}
                    placeholder={props.intl.formatMessage({ id: "IDS_TATUNIT" })}
                   // isMandatory={!needUnit}
                    options={period || []}
                    optionId='ntatunitcode'
                    optionValue='statunitname'
                    value={props.selectedRecord ? props.selectedRecord["ntatunitcode"] : ""}
                    onChange={value => props.onComboChange(value, "ntatunitcode", 1)}
                    alphabeticalSort={true}
                >
                </FormSelectSearch>
                

                </Col>
                </Row>


               <FormInput
                    name={"stestplatform"}
                    label={props.intl.formatMessage({ id: "IDS_TESTPLATFORM" })}
                    type="text"
                    onChange={(event) => props.onInputOnChange(event, 1)}
                    placeholder={props.intl.formatMessage({ id: "IDS_TESTPLATFORM" })}
                    value={props.selectedRecord?props.selectedRecord["stestplatform"]:""}
                    required={false}
                    maxLength={100}
                />

                {/* <FormTextarea
                    name={"stestplatform"}
                    label={props.intl.formatMessage({ id: "IDS_TESTPLATFORM" })}
                    onChange={(event) => props.onInputOnChange(event, 1)}
                    placeholder={props.intl.formatMessage({ id: "IDS_TESTPLATFORM" })}
                    value={props.selectedRecord?props.selectedRecord["stestplatform"]:""}
                    rows="2"
                    required={false}
                    maxLength={255}
                >
                </FormTextarea> */}
  
{/* 
                <FormNumericInput
                    name={"ndeltacheckframe"}
                    label={props.intl.formatMessage({ id: "IDS_DELTACHECKTIMEFRAME" })}
                    type="number"
                    value={props.selectedRecord["ndeltacheckframe"]}
                    placeholder={props.intl.formatMessage({ id: "IDS_DELTACHECKTIMEFRAME" })}
                    strict={true}
                    min={0}
                    maxLength={3}
                    onChange={(value) => props.onNumericInputChange(value, "ndeltacheckframe")}
                    noStyle={true}
                    className="form-control"
                    errors="Please provide a valid number."
                />
                 <FormSelectSearch
                    formLabel={props.intl.formatMessage({ id: "IDS_DELTACHECKTIMEUNIT" })}
                    isSearchable={true}
                    name={"nperiodcode"}
                    placeholder={props.intl.formatMessage({ id: "IDS_DELTACHECKTIMEUNIT" })}
                    options={period || []}
                    optionId='nperiodcode'
                    optionValue='speriodname'
                    value={props.selectedRecord ? props.selectedRecord["ndeltaunitcode"] : ""}
                    onChange={value => props.onComboChange(value, "ndeltaunitcode", 1)}
                    alphabeticalSort={true}
                >
                </FormSelectSearch>

                <CustomSwitch
                    name={"ndeltacheck"}
                    label={props.intl.formatMessage({ id: "IDS_DELTACHECK"})}
                    type="switch"
                    onChange={(event)=>props.onInputOnChange(event, 1, [3, 4])}
                    placeholder={props.intl.formatMessage({ id: "IDS_DELTACHECK"})}
                    defaultValue ={props.selectedRecord["ndeltacheck"] === 3 ? true :false}
                    checked={props.selectedRecord["ndeltacheck"] === 3 ? true :false}
                >
                </CustomSwitch> */}

                {/* { props.needOtherTest && 
            <AddTestSection
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                section={props.otherTestData.section}
                isMulti={false}
            />} */}

                </Col>
            {/* { props.needOtherTest && 
            <AddTestSection
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                section={props.otherTestData.section}
                isMulti={false}
            />}
            { props.needOtherTest && 
            <AddTestMethod
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                method={props.otherTestData.method}
                isMulti={false}
            />} */}
            {/* { props.needOtherTest && 
            <AddTestInstrumentCategory
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                instrumentcategory={props.otherTestData.instrumentcategory}
                isMulti={false}
            />}

        { props.needOtherTest && 
            <AddTestPackageTest
                onComboChange={props.onComboChange}
                selectedRecord={props.selectedRecord}
                package={props.otherTestData.package}
                isMulti={false}
            />} */}
            
            
                   
        </Row>
     );
}

export default injectIntl(AddTest);
