                        selectedRecord["sversion"] = ""
                        dispatch({
                            type: DEFAULT_RETURN,
                            payload: {
                                loading: false,
                                comboData: { ...returnObj.comboData },
                                selectedRecord,
                                AgaramTree: [],
                                ActiveKey: [],
                                FocusKey: [],
                                OpenNodes: [],
                                Test: [],
                                SelectedTest: [],
                                selectedSpec: {},
                                Component: [],
                                selectComponent: {},
                                selectedComponent: {},
                                Specification: []
                            }
                        })
                    }
                }
                else {
                    dispatch({
                        type: DEFAULT_RETURN,
                        payload: {
                            loading: false,
                            comboData: { ...returnObj.comboData },
                            selectedRecord
                        }
                    })
                }
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
