  callService,
  crudMaster,
  validateEsignCredential,
  updateStore,
  getDesignTemplateMappingDetail,
  getDesignTemplateMappingComboService,
  filterColumnData,
  reloadDesignTemplateMapping,
  getTMPFilterRegType,
  getTMPFilterRegSubType,
  getTMPFilterSubmit
})(injectIntl(DesignTemplateMapping));

