import React from 'react'
import { connect } from 'react-redux';
import { FormattedMessage, injectIntl } from 'react-intl';
import { Row, Col, Nav, Card, Button, FormGroup, FormLabel } from 'react-bootstrap';
import { Grid, GridColumn } from '@progress/kendo-react-grid';
import { process } from '@progress/kendo-data-query';
import { toast } from 'react-toastify';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faSave, faCopy, faPlus, faSync, faPencilAlt } from '@fortawesome/free-solid-svg-icons';
import SlideOutModal from '../../components/slide-out-modal/SlideOutModal';
import Esign from '../audittrail/Esign';
import ListMaster from '../../components/list-master/list-master.component';
import CustomTab from '../../components/custom-tabs/custom-tabs.component'
import { showEsign, getControlMap, constructOptionList, sortData, getSameRecordFromTwoArrays, onDropAttachFileList, deleteAttachmentDropZone, create_UUID } from '../../components/CommonScript';

import {
    callService, updateStore, crudMaster, filterColumnData, validateEsignCredential, viewAttachment

} from '../../actions';
import {
    initialcombochangeget, addSafetyInstructions, getAddMaterialPopup, getMaterialDetails, addMaterialProperty,
    getMaterialEdit, getMaterialByTypeCode, getAddMaterialSectionPopup, getMaterialSectionEdit, addMaterialFile
} from '../../actions/MaterialAction';

import { DEFAULT_RETURN } from '../../actions/LoginTypes';
import { transactionStatus, attachmentType } from '../../components/Enumeration';
import { ContentPanel, AtSubCard, ReadOnlyText } from '../../components/App.styles';
import ConfirmMessage from '../../components/confirm-alert/confirm-message.component';
import BreadcrumbComponent from '../../components/Breadcrumb.Component';
import ReactTooltip from 'react-tooltip';
import MaterialFilter from './MaterialFilter';
import DynamicSlideout from '../dynamicpreregdesign/DynamicSlideout';
import MaterialSectionTab from './MaterialSectionTab';
import AddMaterialSection from './AddMaterialSection';
import { Affix } from 'rsuite';
import MaterialFileTab from './MaterialFileTab';
import AddMaterialSafetyInstructionTab from './AddMaterialSafetyInstructionTab';
import AddMaterialFile from './AddMaterialFile';


const mapStateToProps = state => {
    return ({ Login: state.Login })
}

class Material extends React.Component {
    constructor(props) {
        super(props);
        const dataState = {
            skip: 0,
            take: this.props.Login.settings ? parseInt(this.props.Login.settings[14]) : 5
        };
        this.searchFieldList = [{ feild: "IDS_MATERIALNAME", jsonfeild: "jsondata" }]
        this.state = {
            masterStatus: "",
            sectionDataState: { skip: 0, take: 10 },
            error: "",
            isOpen: false,
            ScreenRightsData: [], userRoleData: [],
            availableDatas: [],
            selectedRecord: {},
            userroleList: [],
            operation: "",
            comboitem: undefined,
            screenName: undefined,
            SelectedScreenrights: undefined,
            selectedcombo: undefined, selectedcomboUserRole: undefined,
            isSelectedRecordChange: false,
            selectedcombouser: undefined,
            userrnameList: [],
            selectedListmasterSwitch: undefined,
            custombuttonstate: false,
            ControlRightsParent: [],


            userRoleControlRights: [],
            ControlRights: undefined,
            ConfirmDialogScreen: false,
            controlMap: new Map(),
            showAccordian: true,
            dataResult: [],
            skip: 0,
            take: this.props.Login.settings && this.props.Login.settings[3],
            dataState: dataState,
            columnName: '',
            rowIndex: 0,
            data: [],
            splitChangeWidthPercentage: 30,
        };
        this.searchRef = React.createRef();
        this.ControlButton = [{ value: 1, label: this.props.intl.formatMessage({ id: "IDS_ENABLEALLCONTROLRIGHTS" }) },
        { value: 2, label: this.props.intl.formatMessage({ id: "IDS_DISABLEALLCONTROLRIGHTS" }) },
        { value: 3, label: this.props.intl.formatMessage({ id: "IDS_ENABLEALLESIGNRIGHTS" }) },
        { value: 4, label: this.props.intl.formatMessage({ id: "IDS_DISABLEALLESIGNRIGHTS" }) }
        ];
        this.confirmMessage = new ConfirmMessage();
    }

    expandChange = event => {
        const isExpanded =
            event.dataItem.expanded === undefined
                ? event.dataItem.aggregates
                : event.dataItem.expanded;
        event.dataItem.expanded = !isExpanded;
        this.setState({ ...this.state });
    };

    static getDerivedStateFromProps(props, state) {
        if (props.Login.masterStatus !== "" && props.Login.masterStatus !== state.masterStatus) {
            toast.warn(props.Login.masterStatus);
            props.Login.masterStatus = "";
        }
        if (props.Login.error !== state.error) {
            toast.error(props.Login.error)
            props.Login.error = "";
        }
        return null;
    }


    paneSizeChange = (d) => {
        this.setState({
            splitChangeWidthPercentage: d
        })
    }
    render() {
        console.log('fgvvvvvvvvv :', this.props.Login.isSelectedRecordChange)
        console.log('this.props.Login.masterData :', this.props.Login.masterData)
        console.log('this.props :', this.props)
        this.props.Login.masterData.ScreenRights && sortData(this.props.Login.masterData.ScreenRights, '', 'nusersrolehidescreencode')
        const searchedData = this.props.Login.masterData.searchedData
        const ScreenRights = this.props.Login.masterData.ScreenRights || [];
        const addId = this.state.controlMap.has("AddMaterial") && this.state.controlMap.get("AddMaterial").ncontrolcode;
        const editId = this.state.controlMap.has("EditMaterial") && this.state.controlMap.get("EditMaterial").ncontrolcode;
        const deleteId = this.state.controlMap.has("DeleteMaterial") && this.state.controlMap.get("DeleteMaterial").ncontrolcode;
        const addParameterId = this.state.controlMap.has("AddMaterialSafetyInstructions")
            && this.state.controlMap.get("AddMaterialSafetyInstructions").ncontrolcode;
        const addMaterialProperty = this.state.controlMap.has("AddMaterialProperties")
            && this.state.controlMap.get("AddMaterialProperties").ncontrolcode;
        let checkboxListL = []
        checkboxListL = [{
            label: 'inflammable'
        }, { label: 'Keep Refrigerated' }, { label: 'No Acids' }, { label: 'No Bases' }, { label: 'No Oxidizer' }
            , { label: 'Corrosive' }, { label: 'Hazardous' }
        ]
        let checkboxListR = []
        checkboxListR = [{
            label: 'Avoid Exposure To Light'
        }, { label: 'Personal Protective Equipment' }, { label: 'Keep Frozen' }, { label: 'Keep Desiccated' }
            , { label: 'Clothing' }
            , { label: 'Goggles' }, { label: 'Gloves' }
        ]

        let masterlistmain = []
        this.props.Login.masterData.Material &&
            this.props.Login.masterData.Material.map((temp, i) => {
                return masterlistmain.push(this.props.Login.masterData.Material[i].jsondata)
            })
        let searchedDatalistmain = []

        if (this.props.Login.masterData.searchedData === undefined) {
            searchedDatalistmain = undefined
        } else {
            this.props.Login.masterData.searchedData &&
                this.props.Login.masterData.searchedData.map((temp, i) => {
                    return searchedDatalistmain.push(this.props.Login.masterData.searchedData[i].jsondata)
                })
        }


        let selectedmasterlistmain = []
        this.props.Login.masterData.SelectedMaterial &&
            selectedmasterlistmain.push(this.props.Login.masterData.SelectedMaterial.jsondata)
        const filterParam = {
            inputListName: "Material", selectedObject: "SelectedMaterial", primaryKeyField: "nmaterialcode",
            fetchUrl: "material/getMaterialByID", fecthInputObject: { userinfo: this.props.Login.userInfo },
            masterData: this.props.Login.masterData,
            isjsondata: true,
            searchFieldList: this.searchFieldList
        };
        let mandatoryFieldsScreenRights = []
        let mandatoryFieldsMaterialFile = []
        if (this.props.Login.masterData.SelectedMaterialType) {
            if (this.props.Login.masterData.SelectedMaterialType.length > 0) {
                if (this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 1) {
                    mandatoryFieldsScreenRights = [
                        {
                            "mandatory": false, "idsName": "IDS_MATERIALCAT", "dataField": "IDS_MATERIALCAT",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_STANDARDMATERIALNAME", "dataField": "IDS_STANDARDMATERIALNAME",
                            "mandatoryLabel": "IDS_ENTER", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_BASICUOM", "dataField": "IDS_BASICUOM",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },
                    ];
                }
                else if (this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 2) {
                    mandatoryFieldsScreenRights = [
                        {
                            "mandatory": false, "idsName": "IDS_MATERIALCAT", "dataField": "IDS_MATERIALCAT",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_VOLUMETRICMATERIALNAME", "dataField": "IDS_VOLUMETRICMATERIALNAME",
                            "mandatoryLabel": "IDS_ENTER", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_BASICUOM", "dataField": "IDS_BASICUOM",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },
                    ];
                }
                else if (this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3) {
                    mandatoryFieldsScreenRights = [
                        {
                            "mandatory": false, "idsName": "IDS_MATERIALCAT", "dataField": "IDS_MATERIALCAT",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_MATERIALNAME", "dataField": "IDS_MATERIALNAME",
                            "mandatoryLabel": "IDS_ENTER", "controlType": "selectbox"
                        },
                        {
                            "mandatory": false, "idsName": "IDS_BASICUOM", "dataField": "IDS_BASICUOM",
                            "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
                        },

                        {
                            "mandatory": false, "idsName": "IDS_PREFIX", "dataField": "IDS_PREFIX",
                            "mandatoryLabel": "IDS_ENTER", "controlType": "selectbox"
                        }
                    ];
                }
            }
        }

        this.props.Login.screenname === 'IDS_MATERIALFILE' && (this.props.Login.selectedRecord &&
            this.props.Login.selectedRecord.nattachmenttypecode === attachmentType.LINK) ?
            mandatoryFieldsMaterialFile = [
                { "idsName": "IDS_FILENAME", "dataField": "slinkfilename", "mandatory": true, "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" },
                { "idsName": "IDS_LINKNAME", "dataField": "nlinkcode", "mandatory": true, "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox" }
            ]
            :
            mandatoryFieldsMaterialFile = [
                { "idsName": "IDS_FILE", "dataField": "sfilename", "mandatory": true, "mandatoryLabel": "IDS_CHOOSE", "controlType": "file" },
            ]

        const mandatoryFieldsMaterialSection = [
            {
                "mandatory": false, "idsName": "IDS_MATERIALSECTION", "dataField": "nmaterialsectioncode",
                "mandatoryLabel": "IDS_SELECT", "controlType": "selectbox"
            }
        ];


        const breadCrumbData = this.state.filterData || [];
        return (
            <>
                <ReactTooltip place="bottom" globalEventOff='click' id="tooltip_list_wrap" />
                <div className="client-listing-wrap mtop-4">
                    {breadCrumbData.length > 0 ?
                        <Affix top={64}>
                            <BreadcrumbComponent breadCrumbItem={breadCrumbData} />
                        </Affix>
                        : ""
                    }
                    <Row noGutters>
                        <Col md={4} >

                            <ListMaster
                                formatMessage={this.props.intl.formatMessage}
                                screenName={"Material"}
                                masterData={this.props.Login.masterData}
                                userInfo={this.props.Login.userInfo}
                                masterList={searchedDatalistmain || masterlistmain}
                                getMasterDetail={(master) => this.props.getMaterialDetails(master, this.props.Login.masterData, this.props.Login.userInfo)}
                                selectedMaster={this.props.Login.masterData.SelectedMaterial && this.props.Login.masterData.SelectedMaterial.jsondata}
                                primaryKeyField="nmaterialcode"
                                mainField={this.props.Login.masterData.SelectedMaterialType ?
                                    this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 1 ?
                                        "IDS_STANDARDMATERIALNAME" :
                                        this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 2 ?
                                            "IDS_VOLUMETRICMATERIALNAME" :
                                            "IDS_MATERIALNAME" : ""}


                                firstField="IDS_MATERIALNAME"
                                isIDSField="Yes"
                                filterColumnData={this.props.filterColumnData}
                                filterParam={filterParam}
                                userRoleControlRights={this.state.userRoleControlRights}
                                addId={addId}
                                searchRef={this.searchRef}
                                reloadData={this.reloadData}
                                hidePaging={false}
                                openFilter={this.openFilter}
                                closeFilter={this.closeFilter}
                                showFilterIcon={true}
                                onFilterSubmit={this.onFilterSubmit}
                                filterComponent={[
                                    {
                                        "IDS_FILTER":
                                            <MaterialFilter
                                                filterUserRole={this.state.userroleList || []}
                                                userrnameList={this.state.userrnameList || []}
                                                selectedRecord={this.state.selectedcombo || {}}//
                                                selectedcombouser={this.state.selectedcombouser || {}}
                                                onComboChange={this.onComboChange}
                                            />
                                    }
                                ]}
                                openModal={() => this.props.getAddMaterialPopup("IDS_MATERIAL",
                                    "create", this.props.Login.userInfo, this.state.selectedcombo,
                                    this.state.selectedcombouser, addId, this.props.Login.masterData,
                                    this.state.selectedRecord)}
                            />
                        </Col>
                        <Col md='8'>
                            <Row>
                                <Col md={12}>
                                    <ContentPanel className="panel-main-content">
                                        {this.props.Login.masterData.Material && this.props.Login.masterData.Material.length > 0 && this.props.Login.masterData.SelectedMaterial ?
                                            <Card className="border-0">
                                                <Card.Header>
                                                    <Card.Title className="product-title-main">Material</Card.Title>
                                                    <Card.Subtitle>
                                                        <div className="d-flex product-category">
                                                            <h2 className="product-title-sub flex-grow-1">

                                                            </h2>
                                                            <div className="d-inline ">
                                                                <ReactTooltip place="bottom" globalEventOff='click' id="tooltip_list_wrap" />

                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_EDIT" })}
                                                                    data-for="tooltip_list_wrap"
                                                                    onClick={(e) =>
                                                                        this.props.getAddMaterialPopup("IDS_MATERIAL",
                                                                            "update", this.props.Login.userInfo, this.state.selectedcombo,
                                                                            this.state.selectedcombouser, addId, this.props.Login.masterData,
                                                                            this.state.selectedRecord)
                                                                    }
                                                                >

                                                                    <FontAwesomeIcon icon={faPencilAlt}
                                                                        title={this.props.intl.formatMessage({ id: "IDS_EDIT" })} />
                                                                </Nav.Link>

                                                                <Nav.Link className="btn btn-circle outline-grey mr-2 action-icons-wrap" href=""
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_DELETE" })}
                                                                    data-for="tooltip_list_wrap"
                                                                    onClick={() => this.ConfirmDelete(deleteId)}
                                                                >
                                                                    <FontAwesomeIcon icon={faTrashAlt} />

                                                                </Nav.Link>

                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_ADDMATERIALSAFETYINSTRUCTIONS" })}
                                                                    data-for="tooltip_list_wrap"
                                                                    onClick={(e) => this.props.addSafetyInstructions(this.props.Login.masterData,
                                                                        this.props.Login.userInfo, addParameterId, "create", {})}
                                                                >

                                                                    <FontAwesomeIcon icon={faPencilAlt}
                                                                        title={this.props.intl.formatMessage({ id: "IDS_ADDMATERIALSAFETYINSTRUCTIONS" })} />
                                                                </Nav.Link>
                                                                <Nav.Link className="btn btn-circle outline-grey mr-2" href="#"
                                                                    data-tip={this.props.intl.formatMessage({ id: "IDS_ADDMATERIALPROPERTY" })}
                                                                    data-for="tooltip_list_wrap"
                                                                    onClick={(e) => this.props.addMaterialProperty(this.props.Login.masterData,
                                                                        this.props.Login.userInfo, addMaterialProperty, "create", {})}
                                                                >

                                                                    <FontAwesomeIcon icon={faPencilAlt}
                                                                        title={this.props.intl.formatMessage({ id: "IDS_ADDMATERIALPROPERTY" })} />
                                                                </Nav.Link>
                                                            </div>
                                                        </div>
                                                    </Card.Subtitle>
                                                </Card.Header>
                                                <Card.Body>

                                                    <Row>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_MATERIALCAT'} message="Material Category" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_MATERIALCAT.label}
                                                                </ReadOnlyText>
                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={
                                                                    this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 ?
                                                                        'IDS_MATERIALNAME' :
                                                                        this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 1 ?
                                                                            'IDS_STANDARDMATERIALNAME' :
                                                                            this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 2 ?
                                                                                'IDS_VOLUMETRICMATERIALNAME' : ''



                                                                } message="Material" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&
                                                                        this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 ?
                                                                        selectedmasterlistmain[0].IDS_MATERIALNAME :
                                                                        this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 1 ?
                                                                            selectedmasterlistmain[0].IDS_STANDARDMATERIALNAME :
                                                                            this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 2 ?
                                                                                selectedmasterlistmain[0].IDS_VOLUMETRICMATERIALNAME : ''}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_BASICUOM'} message="Basic Units" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_BASICUOM.label}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        {this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_PREFIX'} message="Prefix" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&

                                                                            selectedmasterlistmain[0].IDS_PREFIX}
                                                                    </ReadOnlyText>

                                                                </FormGroup>
                                                            </Col>
                                                        }
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_REORDERLEVEL'} message="Reorder Level" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_REORDERLEVEL}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_OPENEXIPIRY'} message="Open Expiry" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_OPENEXIPIRY}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_OPENEXIPIRYPERIOD'} message="Open Expiry Name" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_OPENEXIPIRYPERIOD.label}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_NEXTVALIDATION'} message="Next Validation Name" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_NEXTVALIDATION}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>

                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_NEXTVALIDATIONPERIOD'} message="Next Validation Period" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_NEXTVALIDATIONPERIOD.label}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_EXPIRYPOLICY'} message="Expiry Policy" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_EXPIRYPOLICY}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_EXPIRYPOLICYPERIOD'} message="Expiry Policy Period" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&

                                                                        selectedmasterlistmain[0].IDS_EXPIRYPOLICYPERIOD.label}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>
                                                        {this.props.Login.masterData.SelectedMaterialType &&
                                                            this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 1 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_REMARKS'} message="Remarks" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&

                                                                            selectedmasterlistmain[0].IDS_REMARKS}
                                                                    </ReadOnlyText>

                                                                </FormGroup>
                                                            </Col>
                                                        }
                                                        {this.props.Login.masterData.SelectedMaterialType &&
                                                            this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 2 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_REMARKS'} message="Remarks" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&

                                                                            selectedmasterlistmain[0].IDS_REMARKS}
                                                                    </ReadOnlyText>

                                                                </FormGroup>
                                                            </Col>
                                                        }
                                                        <Col md='4'>
                                                            <FormGroup>
                                                                <FormLabel><FormattedMessage id={'IDS_QUARANTINE'} message="Quarentine" /></FormLabel>
                                                                <ReadOnlyText>
                                                                    {selectedmasterlistmain.length > 0 &&
                                                                        selectedmasterlistmain[0].IDS_QUARANTINENAME &&
                                                                        selectedmasterlistmain[0].IDS_QUARANTINENAME}
                                                                </ReadOnlyText>

                                                            </FormGroup>
                                                        </Col>

                                                        {this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_NOEXPIRY'} message="No Expiry" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&
                                                                            selectedmasterlistmain[0].IDS_NOEXPIRYNAME &&
                                                                            selectedmasterlistmain[0].IDS_NOEXPIRYNAME}
                                                                    </ReadOnlyText>

                                                                </FormGroup>

                                                            </Col>
                                                        }
                                                        {this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_STORAGECONDITION'} message="Storage Condition" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&
                                                                            selectedmasterlistmain[0].IDS_STORAGECONDITION.label &&
                                                                            selectedmasterlistmain[0].IDS_STORAGECONDITION.label}
                                                                    </ReadOnlyText>

                                                                </FormGroup>

                                                            </Col>
                                                        }


                                                        {this.props.Login.masterData.SelectedMaterialType && this.props.Login.masterData.SelectedMaterialType[0].nmaterialtypecode === 3 &&
                                                            <Col md='4'>
                                                                <FormGroup>
                                                                    <FormLabel><FormattedMessage id={'IDS_REUSABLE'} message="Reusable" /></FormLabel>
                                                                    <ReadOnlyText>
                                                                        {selectedmasterlistmain.length > 0 &&
                                                                            selectedmasterlistmain[0].IDS_REUSABLENAME &&
