    onSaveMaterialFile = () => {
        const selectedRecord = this.state.selectedRecord;
        const acceptedFiles = selectedRecord.sfilename;
        const nattachmenttypecode = selectedRecord.nattachmenttypecode;
        let isFileEdited = transactionStatus.NO;
        let testFileArray = [];
        let testFile = {
            nmaterialcode: this.props.Login.masterData.SelectedMaterial.jsondata.nmaterialcode,
            nmaterialfilecode: selectedRecord.nmaterialfilecode ? selectedRecord.nmaterialfilecode : 0,
            nstatus: transactionStatus.ACTIVE,
            nattachmenttypecode,
            ndefaultstatus: selectedRecord[nattachmenttypecode === attachmentType.LINK ? "nlinkdefaultstatus" : "ndefaultstatus"] ? selectedRecord[nattachmenttypecode === attachmentType.LINK ? "nlinkdefaultstatus" : "ndefaultstatus"] : 4
        };
        const formData = new FormData();
        if (nattachmenttypecode === attachmentType.FTP) {
            if (acceptedFiles && Array.isArray(acceptedFiles) && acceptedFiles.length > 0) {
                acceptedFiles.forEach((file, index) => {
                    const tempData = Object.assign({}, testFile);
                    const splittedFileName = file.name.split('.');
                    const fileExtension = file.name.split('.')[splittedFileName.length - 1];
                    const ssystemfilename = selectedRecord.ssystemfilename ? selectedRecord.ssystemfilename.split('.') : "";
                    const filesystemfileext = selectedRecord.ssystemfilename ? file.name.split('.')[ssystemfilename.length - 1] : "";
                    const uniquefilename = nattachmenttypecode === attachmentType.FTP ? selectedRecord.ntestfilecode && selectedRecord.ntestfilecode > 0
                        && selectedRecord.ssystemfilename !== "" ? ssystemfilename[0] + '.' + filesystemfileext : create_UUID() + '.' + fileExtension : "";
                    tempData["sfilename"] = file.name;
                    tempData["sdescription"] = selectedRecord.sdescription ? selectedRecord.sdescription.trim() : "";
                    tempData["nlinkcode"] = transactionStatus.NA;
                    tempData["ssystemfilename"] = uniquefilename;
                    tempData["nfilesize"] = file.size;
                    formData.append("uploadedFile" + index, file);
                    formData.append("uniquefilename" + index, uniquefilename);
                    testFileArray.push(tempData);
                });
                formData.append("filecount", acceptedFiles.length);
                isFileEdited = transactionStatus.YES;
            } else {
                testFile["sfilename"] = selectedRecord.sfilename;
                testFile["sdescription"] = selectedRecord.sdescription ? selectedRecord.sdescription.trim() : "";
                testFile["nlinkcode"] = transactionStatus.NA;
                testFile["ssystemfilename"] = selectedRecord.ssystemfilename;
                testFile["nfilesize"] = selectedRecord.nfilesize;
                testFileArray.push(testFile);
            }
        } else {
            testFile["sfilename"] = selectedRecord.slinkfilename.trim();
            testFile["sdescription"] = selectedRecord.slinkdescription ? selectedRecord.slinkdescription.trim() : "";
            testFile["nlinkcode"] = selectedRecord.nlinkcode.value ? selectedRecord.nlinkcode.value : -1;
            testFile["ssystemfilename"] = "";
            testFile["nfilesize"] = 0;
            testFileArray.push(testFile);
        }
        formData.append("isFileEdited", isFileEdited);
        formData.append("materialfile", JSON.stringify(testFileArray[0]));

        let inputParam;
        if (this.props.Login.operation === 'create') {
            inputParam = {
                inputData: { userinfo: this.props.Login.userInfo },
                formData: formData,
                isFileupload: true,
                operation: "create",
                classUrl: "material",
                methodUrl: "MaterialFile"
            }
        }
        else {
            inputParam = {
                inputData: { userinfo: this.props.Login.userInfo },
                formData: formData,
                isFileupload: true,
                operation: "update",
                classUrl: "material",
                methodUrl: "MaterialFile"
            }
        }

        return inputParam;
    }


    getPreviewTemplate = (masterData, userInfo, editId) => {
        let data = [];
        const withoutCombocomponent = []
        const Layout = JSON.parse(this.props.Login.templateData)
        Layout.map(row => {
            row.children.map(column => {
                column.children.map(component => {
                    return component.hasOwnProperty("children") ? component.children.map(componentrow => {
                        if (componentrow.inputtype === "combo") {
                            data.push(componentrow)
                        } else {
                            withoutCombocomponent.push(componentrow)
                        }
                    })
                        : component.inputtype === "combo" ? data.push(component) : withoutCombocomponent.push(component)
                })
            })
        })
        const comboComponents = data
        this.props.getPreviewTemplate(masterData, userInfo, editId, data, this.state.selectedRecord, null, comboComponents, withoutCombocomponent)

    }

    getPreviewTemplateOnchange = (comboData, control) => {
        let data = [];
        const withoutCombocomponent = []
        const Layout = JSON.parse(this.props.Login.templateData)
        Layout.map(row => {
            row.children.map(column => {
                column.children.map(component => {
                    return component.hasOwnProperty("children") ? component.children.map(componentrow => {
                        if (componentrow.inputtype === "combo") {
                            data.push(componentrow)
                        } else {
                            withoutCombocomponent.push(componentrow)
                        }
                    })
                        : component.inputtype === "combo" ? data.push(component) : withoutCombocomponent.push(component)
                })
            })
        })
        const comboComponents = data
        const childComboList = getSameRecordFromTwoArrays(comboComponents, control.child, "label")
        let childColumnList = {};
        childComboList.map(columnList => {
            const val = this.comboChild(comboComponents, columnList, childColumnList, false);
            childColumnList = val.childColumnList
        })

        const parentList = getSameRecordFromTwoArrays(withoutCombocomponent, control.child, "label")

        const inputParem = {
            child: control.child,
            source: control.source,
            primarykeyField: control.valuemember,
            value: comboData.value,
            item: comboData.item
        }
        this.props.getChildValues(inputParem,
            this.props.Login.userInfo, this.state.selectedRecord, this.props.Login.comboData,
            childComboList, childColumnList, withoutCombocomponent, [...childComboList, ...parentList])
    }

