        else {
            this.props.copyScreenRights(this.state.selectedRecord ? this.state.selectedRecord["nuserrole"].value : "", this.state.selectedcombo["nuserrolecode"] ? this.state.selectedcombo["nuserrolecode"].value : "", this.props.Login.userInfo, this.props.Login.masterData);
        }
    }

    onSaveAllControlAndEsign = (skip, take, saveType, formRef) => {
        let value = this.state.comboitem ? this.state.comboitem.value.value : this.ControlButton[0].value
        let operation = "";
        let methodUrl = "";
        let inputData = [];
        let postParam = undefined;
        inputData["userinfo"] = this.props.Login.userInfo;
        inputData["selectedscreenrights"] = this.props.Login.masterData.SelectedScreenRights;
        inputData["nflag"] = transactionStatus.DEACTIVE;
        postParam = {
            inputListName: "ScreenRights", selectedObject: "SelectedScreenRights",
            primaryKeyField: "nusersrolehidescreencode"
        }
        if (value === 1) {
            inputData["needrights"] = transactionStatus.YES;
            inputData["nneedesign"] = null;
            operation = "update";
            methodUrl = "ControlRights";
        }
        else if (value === 2) {
            inputData["needrights"] = transactionStatus.NO;
            inputData["nneedesign"] = null;
            operation = "update";
            methodUrl = "ControlRights";
        }
        else if (value === 3) {
            inputData["nneedesign"] = transactionStatus.YES;
            operation = "update";
            methodUrl = "Esign";
        }
        else if (value === 4) {
            inputData["nneedesign"] = transactionStatus.NO;
            operation = "update";
            methodUrl = "Esign";
        }

        const inputParam = {
            classUrl: "userscreenhide",
            methodUrl: "ScreenHideControlRights",
            displayName: this.props.Login.inputParam.displayName,
            inputData: inputData,
            operation: operation, saveType,
            formRef, postParam,
            searchRef: this.searchRef,
            selectedcombo: this.props.Login.selectedcombo,
        }
        this.props.crudMaster(inputParam, this.props.Login.masterData, "openModal");
    }

    ConfirmComponent = () => {
        this.confirmMessage.confirm("confirmation", "Confiramtion!", this.props.intl.formatMessage({ id: "IDS_OVERWRITRTHEEXISTINGSCREENRIGHTS" }),
            "ok", "cancel", () => this.copyAlertSave());
    }

    componentWillUnmount() {
        const updateInfo = {
            typeName: DEFAULT_RETURN,
            data: {
                masterData: [], inputParam: undefined, operation: null, modalName: undefined
            }
        }
        this.props.updateStore(updateInfo);
    }

    handleClickDelete(masterData, ncontrolcode, modalName) {
        const fieldArray = [];
        let postParam = {
            inputListName: "Material", selectedObject: "SelectedMaterial",
            primaryKeyField: "nmaterialcode",
            primaryKeyValue: fieldArray,
            fetchUrl: "material/getMaterial",
            fecthInputObject: { userinfo: this.props.Login.userInfo },
        }
        modalName = 'openModal'

        const inputParam = {
            methodUrl: "Material",
            classUrl: "material",
            inputData: {
                "material": this.props.Login.masterData.SelectedMaterial,
                "nmaterialcatcode": this.state.selectedcombouser["nmaterialcatcode"].value,
                "userinfo": this.props.Login.userInfo,
                "nmaterialcode": this.props.Login.masterData.SelectedMaterial && this.props.Login.masterData.SelectedMaterial.jsondata.nmaterialcode,
                "nmaterialtypecode": this.state.selectedcombo["nmaterialtypecode"].value
            },
            operation: "delete", postParam,
            displayName: "Material",
        }

        if (showEsign(this.props.Login.userRoleControlRights, this.props.Login.userInfo.nformcode, ncontrolcode)) {
            const updateInfo = {
                typeName: DEFAULT_RETURN,
                data: {
                    loadEsign: true, screenData: { inputParam, masterData }, [modalName]: true,
                    operation: 'delete', screenName: "Material", id: "material"
                }
            }
            this.props.updateStore(updateInfo);
        }
        else {
            this.props.crudMaster(inputParam, masterData, modalName);
        }
    }

    reloadData = () => {
        this.searchRef.current.value = "";
        let inputData = [];
        inputData["nmaterialtypecode"] = this.state.selectedcombo["nmaterialtypecode"];
        inputData["userrnameList"] = this.state.userrnameList;
        const inputParam = {
            classUrl: "material",
            methodUrl: "getMaterialByTypeCode",
            displayName: "Material",
            inputData: inputData
        }
        this.setState({ sectionDataState: { skip: 0, take: 10 } })
        if (this.state.selectedcombo["nmaterialtypecode"]) {
            this.props.getMaterialByTypeCode(inputParam, this.state.selectedcombo, this.state.selectedcombouser,
                this.props.Login.masterData, this.props.Login.userInfo);

        } else {
            toast.warn(this.props.intl.formatMessage({ id: "IDS_SELECTUSERROLE" }));
        }
    }
}

export default connect(mapStateToProps, {
    callService, updateStore, initialcombochangeget, getAddMaterialPopup, viewAttachment,
    crudMaster, getMaterialDetails, getMaterialEdit, getMaterialByTypeCode, addMaterialFile
    , getAddMaterialSectionPopup, getMaterialSectionEdit, filterColumnData, validateEsignCredential, addSafetyInstructions, addMaterialProperty
})(injectIntl(Material));
