                    ntzdreceivedate = component.ntzdreceivedate.value;
                }
                component["ntzdreceivedate"] = component.ntzdreceivedate ?
                    {
                        "label": TimeZoneField[TimeZoneField.findIndex(x => x.ntimezonecode === ntzdreceivedate)].stimezoneid, "value":
