import rsapi from '../rsapi';
import { DEFAULT_RETURN } from './LoginTypes';
import { toast } from 'react-toastify';
import { initRequest } from './LoginAction';
import { constructOptionList, replaceUpdatedObject, sortData,fillRecordBasedOnCheckBoxSelection,getSameRecordFromTwoArrays,
    getRecordBasedOnPrimaryKeyName,filterRecordBasedOnPrimaryKeyName,reArrangeArrays,rearrangeDateFormat } from '../components/CommonScript';
import { postCRUDOrganiseTransSearch } from './ServiceAction';
import Axios from 'axios';

export function getRegTypeJobAllocation(inputData) {
    return function (dispatch) {
        dispatch(initRequest(true));
        rsapi.post("joballocation/getRegistrationTypeBySampleType", inputData)
            .then(response => {
                let responseData = { ...response.data }
                responseData = sortData(responseData)
                dispatch({
                    type: DEFAULT_RETURN, payload: {
                        masterData: {
                            ...inputData.masterData,
                            ...responseData,
                            defaultSampleTypeValue: inputData.defaultSampleTypeValue
                        },
                        loading: false
                    }
                })
            })
            .catch(error => {
                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })
                if (error.response.status === 500) {
                    toast.error(error.message);
                }
                else {
                    toast.warn(error.response.data);
                }
            })
    }
}

