        }
      } else {
        if (control.label === 'Transaction Type' // || control.label === 'IDS_SECTION'
        ) {
          this.props.getQuantityTransactionOnchange(comboData, this.props.Login.masterData, this.props.Login.userInfo, selectedRecord, this.props.Login.templateData);
