                            }
                        }
                    })
                })
            })
        })
            if ( this.props.Login.masterData.SelectedMaterialCrumb['jsondata'].needsectionwise === transactionStatus.NO) {
                mandatoryFeildsMaterial.map((temp, i) => {
                    if (temp.idsName === 'Section') {
                        delete mandatoryFeildsMaterial[i];
                    }
                });
            }
        
        return mandatoryFeildsMaterial;
    }

