import React from "react";
import { Scheduler, TimelineView, DayView, WeekView, MonthView, AgendaView } from '@progress/kendo-react-scheduler';
import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import { Col, Row } from "react-bootstrap";
import { injectIntl } from "react-intl";
import { guid } from '@progress/kendo-react-common';
import './Calender.css'
import CustomEditItem, {
    CustomEditSlotForMonth,
    CustomSlot,
    CustomSlotForDayAndWeek,
    CustomTask
} from "./EditItem";
import CustomItem from './NewCustomItem'
import { FormWithCustomEditor } from "./custom-form";
import { toast } from "react-toastify";
import rsapi from "../../rsapi";
import { convertDateTimetoStringDBFormat } from "../../components/CommonScript";

class NewJobAlloct extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [],
            view: 'month',
            startDate:this.props.currentTime
        }
    }

    render() {
        var displayDate = this.props.currentTime;
        return (
            <Row>
            <Col md={12}>
                <Row className="mt-n4">
                    <Col md={8} className="d-flex">
                            <FormSelectSearch
                                formLabel={this.props.intl.formatMessage({ id: "IDS_TECHNIQUE" })}
                                isSearchable={true}
                                name={"ntechniquecode"}
                                isDisabled={false}
                                placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isMandatory={false}
                                isClearable={true}
                                options={this.props.Technique}
                                value={this.props.selectedRecord["ntechniquecode"] || ""}
                                defaultValue={this.props.selectedRecord["ntechniquecode"]}
                                onChange={(event) => this.props.onComboChange(event, "ntechniquecode")}
                                closeMenuOnSelect={true}
                                formGroupClassName={"single-line-label"}
                            >
                            </FormSelectSearch>
                            <FormSelectSearch
                                formLabel={this.props.intl.formatMessage({ id: "IDS_USERS" })}
                                isSearchable={true}
                                name={"nusercode"}
                                isDisabled={false}
                                placeholder={this.props.intl.formatMessage({ id: "IDS_SELECTRECORD" })}
                                isMandatory={true}
                                isClearable={false}
                                options={this.props.Users}
                                value={this.props.selectedRecord["nusercode"] || ""}
                                defaultValue={this.props.selectedRecord["nusercode"]}
                                onChange={(event) => this.props.onComboChange(event, "nusercode")}
                                closeMenuOnSelect={true}
                                formGroupClassName={"single-line-label"}
                            >
                            </FormSelectSearch>
                        </Col>
                    </Row>
                </Col>
                <Col md={12}>
                    <Scheduler data={this.state.data}
                        onDataChange={this.handleDataChange}
                        onDateChange={this.handleDateChange}
                        onViewChange={this.onViewChange}
                        editable={{
                            add: true,
                            remove: true,
                            drag: false,
                            resize: true,
                            select: true,
                            edit: true
                        }}
                        height={600}
                        editItem={CustomEditItem}
                        item={CustomItem}
                        form={FormWithCustomEditor}
                        defaultDate={displayDate}
                        editSlot={CustomEditSlotForMonth}
                        slot={CustomSlot}
                    >
                        <MonthView
                            editable={{
                                add: true,
                                remove: true,
                                drag: false,
                                resize: true,
                                select: true,
                                edit: true
                            }}
                        />
                        <AgendaView slotDivisions={1} task={CustomTask} editable={{
                            add: false,
                            remove: false,
                            drag: false,
                            resize: false,
                            select: false,
                            edit: false
                        }} />
                        <WeekView
                            slot={CustomSlotForDayAndWeek}
                            editItem={CustomEditItem}
                            editSlot={CustomEditSlotForMonth}
                            item={CustomItem}
                        />
                        <DayView
                            slot={CustomSlotForDayAndWeek}
                            editSlot={CustomEditSlotForMonth}
                            editItem={CustomEditItem}
                            item={CustomItem}
                            numberOfDays={1}
                            style={{ zorder: 25000 }}
                        />
                        <TimelineView 
                            slot={CustomSlotForDayAndWeek}
                        />
                    </Scheduler>
                </Col>
            </Row>
        );
    }

    handleDateChange = (e) => {
        const date = e.value
        const view = this.state.view
        return rsapi.post("joballocation/getAnalystCalendarBasedOnUserWithDate", {
            "nusercode": this.props.selectedRecord["nusercode"] ? this.props.selectedRecord["nusercode"].value : -1, view: view, startDate: convertDateTimetoStringDBFormat(date, this.props.userInfo),
            "userinfo": this.props.userInfo
        })
            .then(response => {
                let data = [];
                for (let i = 0; i < response.data.UserData.length; i++) {
                    data.push({ ...response.data.UserData[i].jsondata, ...response.data.UserData[i] });
                }
                data = data.map((dataItem, i) => ({
                    start: new Date(dataItem.UserStartDate),
                    end: new Date(dataItem.UserEndDate),
                    title: `${dataItem.sarno && dataItem.sarno}/${dataItem.stestsynonym && dataItem.stestsynonym}`,
                    // title: `ArNo:${dataItem.sarno && dataItem.sarno} - Test Name:${dataItem.stestsynonym && dataItem.stestsynonym} - Status:${dataItem.stransdisplaystatus && dataItem.stransdisplaystatus}`,
                    id: dataItem.id ? dataItem.id : i,
                    startTimezone: dataItem.startTimezone,
                    endTimezone: dataItem.endTimezone,
                    description: dataItem.Comments ? dataItem.Comments : "",
                    Instrument: dataItem.Instrument.value,
                    InstrumentCategory: dataItem.InstrumentCategory.value,
                    sarno: dataItem.sarno,
                    ssamplearno: dataItem.ssamplearno,
                    stestsynonym: dataItem.stestsynonym,
                    ntransactionstatus: dataItem.ntransactionstatus,
                    stransdisplaystatus: dataItem.stransdisplaystatus,
                    ntransactiontestcode: dataItem.ntransactiontestcode,
                    npreregno: dataItem.npreregno,
                }))
                this.setState({ data: data, startDate: date });
            }).catch(error => {
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }

    onViewChange = (e) => {
        return rsapi.post("joballocation/getAnalystCalendarBasedOnUserWithDate", {
            "nusercode": this.props.selectedRecord["nusercode"] ? this.props.selectedRecord["nusercode"].value : -1, view: e.value, startDate: convertDateTimetoStringDBFormat(this.state.startDate, this.props.userInfo),
            "userinfo": this.props.userInfo
        })
            .then(response => {
                let data = [];
                for (let i = 0; i < response.data.UserData.length; i++) {
                    data.push({ ...response.data.UserData[i].jsondata, ...response.data.UserData[i] });
                }
                data = data.map((dataItem, i) => ({
                    start: new Date(dataItem.UserStartDate),
                    end: new Date(dataItem.UserEndDate),
                    title: `${dataItem.sarno && dataItem.sarno}/${dataItem.stestsynonym && dataItem.stestsynonym}`,
                    id: dataItem.id ? dataItem.id : i,
                    startTimezone: dataItem.startTimezone,
                    endTimezone: dataItem.endTimezone,
                    description: dataItem.Comments ? dataItem.Comments : "",
                    Instrument: dataItem.Instrument.value,
                    InstrumentCategory: dataItem.InstrumentCategory.value,
                    sarno: dataItem.sarno,
                    ssamplearno: dataItem.ssamplearno,
                    stestsynonym: dataItem.stestsynonym,
                    ntransactionstatus: dataItem.ntransactionstatus,
                    stransdisplaystatus: dataItem.stransdisplaystatus,
                    ntransactiontestcode: dataItem.ntransactiontestcode,
                    npreregno: dataItem.npreregno,
                }));
                this.setState({ data: data, view: e.value });
            }).catch(error => {
                if (error.response.status === 500) {
                    toast.error(error.message);
                } else {
                    toast.warn(error.response.data);
                }
            })
    }


    handleDataChange = ({
        created,
        updated,
        deleted
    }) => {
        let data = this.state.data;
        const aa = created.concat(created.map(item => Object.assign({}, item, {
            id: guid()
        })));
        if (aa.length > 1) {
            const dataTest = data.filter(x => x.insertRecord);
            if (dataTest.length !== this.props.selectedTest.length) {
                data = data.filter(item => deleted.find(current => current.id === item.id) === undefined) // Find and replace the updated items
                    .map(item => updated.find(current => current.id === item.id) || item) // Add the newly created items and assign an `id`.
                    .concat(this.props.selectedTest.map((y) => {
                        if (dataTest.filter(x => x.ntransactiontestcode === y.ntransactiontestcode).length === 0) {
                            return Object.assign({}, y, {
                                ...created.map(item => Object.assign({}, item, {
                                    id: guid(), insertRecord: true, title: `${y.sarno && y.sarno}/${y.stestsynonym && y.stestsynonym}`
                                }))[0]
                            })
                        }
                    }
                    ))
                const newArray = data.filter((a) => a)
                this.setState({ data: newArray });
                this.props.parentScheduleState(newArray);
            } else {
                toast.info(this.props.intl.formatMessage({id:"IDS_TESTALREADYASSIGNEDEDITASSIGNED"}))
            }
        }
        if (updated.length > 0) {
            data = data     // Filter the deleted items
                .filter(item => deleted.find(current => current.id === item.id) === undefined) // Find and replace the updated items
                .map(item => updated.find(current => current.id === item.id) || item) // Add the newly created items and assign an `id`.
                .concat(created.map(item => Object.assign({}, item, {
                    id: guid()
                })))
            this.setState({data});
            this.props.parentScheduleState(data);
        }

        if (deleted.length > 0) {
            if (deleted[0].ntransactionstatus !== 20) {
                this.setState(old => ({
                    data: old.data     // Filter the deleted items
                        .filter(item => deleted.find(current => current.id === item.id) === undefined) // Find and replace the updated items
                        .map(item => updated.find(current => current.id === item.id) || item) // Add the newly created items and assign an `id`.
                        .concat(created.map(item => Object.assign({}, item, {
                        })))
                }));
                this.props.parentScheduleState(data);
            } else {
                toast.info(this.props.intl.formatMessage({id:"IDS_ALLOTEDCANNOTBEDELETED"}))
            }
        }
    };

    componentDidUpdate(previousProps) {
        let data = [];
        if (this.props.userData !== undefined) {
            if (!(this.props.userData.length <= this.state.data.length)) {
                for (let i = 0; i < this.props.userData.length; i++) {
                    data.push({ ...this.props.userData[i].jsondata, ...this.props.userData[i] });
                }
                data = data.map((dataItem, i) => ({
                    start: new  Date(dataItem.UserStartDate),
                    end: new Date(dataItem.UserEndDate),
                    title: `${dataItem.sarno && dataItem.sarno}/${dataItem.stestsynonym && dataItem.stestsynonym}`,
                    // title: `ArNo:${dataItem.sarno && dataItem.sarno} - Test Name:${dataItem.stestsynonym && dataItem.stestsynonym} - Status:${dataItem.stransdisplaystatus && dataItem.stransdisplaystatus}`,
                    id: dataItem.id ? dataItem.id : i,
                    startTimezone: dataItem.startTimezone,
                    endTimezone: dataItem.endTimezone,
                    description: dataItem.Comments ? dataItem.Comments : "",
                    Instrument: dataItem.Instrument && dataItem.Instrument.value,
                    InstrumentCategory: dataItem.InstrumentCategory && dataItem.InstrumentCategory.value,
                    sarno: dataItem.sarno,
                    ssamplearno: dataItem.ssamplearno,
                    stestsynonym: dataItem.stestsynonym,
                    ntransactionstatus: dataItem.ntransactionstatus,
                    stransdisplaystatus: dataItem.stransdisplaystatus,
                    ntransactiontestcode: dataItem.ntransactiontestcode,
                    npreregno: dataItem.npreregno,
                }))
                this.setState({ data: data });
            }
        }
    }
}

export default injectIntl(NewJobAlloct);
export const currentYear = new Date().getFullYear();
export const parseAdjust = eventDate => {
    const date = new Date(eventDate);
    date.setFullYear(currentYear);
    return date;
};

export const parseAdjustDate = eventDate => {

    return [
        padTo2Digits(eventDate.getMonth() + 1),
        eventDate.getFullYear(),
        padTo2Digits(eventDate.getDate()),
    ].join('/');
};

export const padTo2Digits = (num) => {
    return num.toString().padStart(2, '0');
}
