            URL=rsapi.post("/client/getClientByCategory", { "userinfo": methodParam.inputData.userinfo, "nclientcatcode":  methodParam.inputData.primarykey  })

         dispatch(initRequest(true));

        Axios.all([URL])

            .then(response => {

                let Client,sclientsiteaddress;
                let SelectedClient=[];
             

                const usertMap = constructOptionList(response[0].data.Client || [], "nclientcode","sclientname", undefined, undefined, false);              

                Client = usertMap.get("OptionList");

                // SelectedClient=response[0].data.selectedClient;
               //  sclientsiteaddress= response[0].data.selectedClientSite.saddress1;
                // selectedRecord={...selectedRecord,...response[0].data.selectedClient}
                // selectedRecord={...selectedRecord,...response[0].data.selectedClientSite.saddress1} 

                if(Client.length>0){
                    
                    selectedRecord["nclientcode"]=  {
                        label: response[0].data.selectedClient.sclientname,
                        value: response[0].data.selectedClient.nclientcode,
                        item: response[0].data.selectedClient
                    }
                    // selectedRecord["nclientcode"]=response[0].data.selectedClient.nclientcode;
                    // selectedRecord["sclientname"]=response[0].data.selectedClient.sclientname;
                    let ClientSite = response[0].data.ClientSite;
                    let ActiveClientSite = ClientSite.filter(x => x.ndefaultstatus === 3);

                    selectedRecord["sclientsiteaddress"]=ActiveClientSite[0].saddress1;
                    // selectedRecord={response[0].data.selectedClientSite.saddress1}
                }
                
               selectedRecord = {...selectedRecord}

                dispatch({ type: DEFAULT_RETURN, payload:

                     { Client,selectedRecord, loading: false, data: undefined, dataState: undefined,sclientsiteaddress,SelectedClient } })

            })

            .catch(error => {

                dispatch({ type: DEFAULT_RETURN, payload: { loading: false } })

                if (error.response.status === 500) {

                    toast.error(error.message);

                }

                else {

                    toast.warn(error.response.data);

                }

            })

    }

}

