import React, { useRef } from "react";
import { Button } from "react-bootstrap";
import { useDrag } from "react-dnd";
import { COMPONENT, COMPONENTROW } from "./constants";

const style = {
  // border: "1px dashed black",
  padding: "0",
  cursor: "move"
};
const Componentexp = ({ data, components, path, onclickcomponent, selectedFieldRecord }) => {
  const ref = useRef(null);

  const [{ isDragging }, drag] = useDrag({
    type: COMPONENTROW,
    item: {
      type: COMPONENTROW,
      id: data.id,
      data: data,
      path
    },
    collect: monitor => ({
      isDragging: monitor.isDragging()
    })
  });

  const opacity = isDragging ? 0 : 1;
  drag(ref);

  // const component = components[data.id];

  return (
    <div
      ref={ref}
      style={{ ...style, opacity }}
      className={`component draggable mr-3 ${selectedFieldRecord.id === data.id ? "selected" : ""} `}
    >
      {/* <div>{data.id}</div> */}
      {/* <Button className={`componentItemButton ${selectedFieldRecord.id === data.id ? "selected" : ""}`} onClick={(e) => onclickcomponent(e, data, path)}> */}
      <div
        className={`componentItem text-center ${selectedFieldRecord.id === data.id ? "selected" : ""}`} //style={{ "whiteSpace": "nowrap" }}
        onClick={(e) => onclickcomponent(e, data, path)}>
        {data.label || data.componentname}
      </div>
      {/* </Button> */}
    </div>
  );
};
export default Componentexp;
