import React from 'react';
import { injectIntl } from 'react-intl';
import {Row, Col} from 'react-bootstrap';

import FormInput from '../../components/form-input/form-input.component';
import FormTextarea from '../../components/form-textarea/form-textarea.component';

import FormSelectSearch from '../../components/form-select-search/form-select-search.component';
import DateTimePicker from '../../components/date-time-picker/date-time-picker.component';


const AddPatient = (props) =>{    
       return (<>

                    <Row>
                         <Col md={12}>                            
                        
                              <FormInput
                                   name={"sfirstname"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_FIRSTNAME"})}                            
                                   placeholder={ props.intl.formatMessage({ id:"IDS_FIRSTNAME"})}
                                   value ={ props.selectedRecord["sfirstname"] }
                                   isMandatory={true}
                                   required={true}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}                            
                              />
                     
                              <FormInput
                                   name={"slastname"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_LASTNAME"})}                          
                                   placeholder={ props.intl.formatMessage({ id:"IDS_LASTNAME"})}
                                   value ={ props.selectedRecord["slastname"]}
                                   isMandatory={true}
                                   required={true}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                              />
              
                              <FormInput
                                   name={"snationalid"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_NATIONALID"})}                   
                                   placeholder={ props.intl.formatMessage({ id:"IDS_NATIONALID"})}
                                   value ={ props.selectedRecord["snationalid"] }
                                   isMandatory={false}
                                   required={false}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                              />

                              <FormInput
                                   name={"sfathername"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_FATHERGUARDIANNAME"})}                   
                                   placeholder={ props.intl.formatMessage({ id:"IDS_FATHERGUARDIANNAME"})}
                                   value ={ props.selectedRecord["sfathername"] }
                                   isMandatory={true}
                                   required={true}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                              />
                      
                              <DateTimePicker
                                                  name={"ddob"} 
                                                  label={ props.intl.formatMessage({ id:"IDS_DATEOFBIRTH"})}                     
                                                  className='form-control'
                                                  placeholderText="Select date.."
                                                  selected={props.selectedRecord["ddob"] || ""}
                                                  dateFormat={props.userInfo.ssitedate}
                                                  isClearable={false}
                                                  isMandatory={true}
                                                  required={true}
                                                  maxDate={props.currentTime}
                                                  maxTime={props.currentTime}
                                                  onChange={date => props.handleDateChange("ddob", date)}
                                                  value={props.selectedRecord["ddob"]}
                                                                                
                                   />
                           
                              <FormInput
                                   name={"sage"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_AGE"})}                        
                                   placeholder={ props.intl.formatMessage({ id:"IDS_AGE"})}
                                   value ={ props.selectedRecord["sage"]}
                                   isMandatory={true}
                                   required={true}
                                   readOnly
                                   onChange={(event)=> props.onInputOnChange(event)}
                              />

                              <FormSelectSearch
                                        name={"ngendercode"}
                                        formLabel={ props.intl.formatMessage({ id:"IDS_SEX"})}                                
                                        placeholder={props.intl.formatMessage({ id:"IDS_SELECTRECORD"})}                                
                                        options={ props.genderList}                                      
                                        value = { props.selectedRecord["ngendercode"] }
                                        isMandatory={true}
                                        isClearable={true}
                                        isMulti={false}
                                        isSearchable={false}                               
                                        isDisabled={false}
                                        closeMenuOnSelect={true}
                                        onChange = {(event)=> props.onComboChange(event, 'ngendercode')}                               
                                   />
                       
                              <FormTextarea
                                   name={"saddress"}
                                   label={ props.intl.formatMessage({ id:"IDS_ADDRESS"})}                    
                                   placeholder={ props.intl.formatMessage({ id:"IDS_ADDRESS"})}
                                   value ={ props.selectedRecord["saddress"]}
                                   rows="2"
                                   isMandatory={true}
                                   required={true}
                                   maxLength={255}
                                   onChange={(event)=> props.onInputOnChange(event)}
                                   />
                        
                              <FormInput
                                   name={"sphoneno"}
                                   type="text"
                                   label={ props.intl.formatMessage({ id:"IDS_PHONENO"})}                  
                                   placeholder={ props.intl.formatMessage({ id:"IDS_PHONENO"})}
                                   value ={ props.selectedRecord["sphoneno"]}
                                   isMandatory={true}
                                   required={true}
                                   maxLength={100}
                                   onChange={(event)=> props.onInputOnChange(event)}
                              /> 
                 
                    </Col> 
       </Row></>
       )
   }
   export default injectIntl(AddPatient);
