})});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});}else{dispatch({type:DEFAULT_RETURN,payload:{multilingualMsg:"IDS_SELECTTEST"}});}};}export function validateEsignforApproval(inputParam){return function(dispatch){dispatch(initRequest(true));return rsapi.post("login/validateEsignCredential",inputParam.inputData).then(function(response){if(response.data==="Success"){if(inputParam.operation==='dynamic'){var methodUrl="performaction";inputParam["screenData"]["inputParam"]["inputData"]["userinfo"]=inputParam.inputData.userinfo;if(inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]&&inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]["esignpassword"]){delete inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]["esignpassword"];delete inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]["esigncomments"];delete inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]["esignreason"];delete inputParam["screenData"]["inputParam"]["inputData"][methodUrl.toLowerCase()]["agree"];}dispatch(performAction(inputParam["screenData"]["inputParam"],inputParam["screenData"]["masterData"]));}else if(inputParam.operation==='reportgeneration'){delete inputParam["screenData"]["inputParam"]['reporparam']["esignpassword"];delete inputParam["screenData"]["inputParam"]['reporparam']["esigncomments"];delete inputParam["screenData"]["inputParam"]['reporparam']["esignreason"];delete inputParam["screenData"]["inputParam"]['reporparam']["agree"];inputParam["screenData"]["inputParam"]["reporparam"]["userinfo"]=inputParam.inputData.userinfo;dispatch(generateCOAReport(inputParam["screenData"]["inputParam"]['reporparam']));}else if(inputParam.operation==='decision'){delete inputParam["screenData"]["inputParam"]["inputData"]['updatedecision']["esignpassword"];delete inputParam["screenData"]["inputParam"]["inputData"]['updatedecision']["esigncomments"];delete inputParam["screenData"]["inputParam"]["inputData"]['updatedecision']["esignreason"];delete inputParam["screenData"]["inputParam"]["inputData"]['updatedecision']["agree"];inputParam["screenData"]["inputParam"]["inputData"]["userinfo"]=inputParam.inputData.userinfo;dispatch(updateDecision(inputParam["screenData"]["inputParam"]));}else if(inputParam.operation==='enforce'){var _methodUrl=inputParam.screenData.inputParam.methodUrl;inputParam["screenData"]["inputParam"]["inputData"]["userinfo"]=inputParam.inputData.userinfo;if(inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]&&inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]["esignpassword"]){delete inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]["esignpassword"];delete inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]["esigncomments"];delete inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]["esignreason"];delete inputParam["screenData"]["inputParam"]["inputData"][_methodUrl.toLowerCase()]["agree"];}dispatch(updateEnforceStatus(inputParam["screenData"].inputParam));}}}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function previewSampleReport(inputParam){return function(dispatch){dispatch(initRequest(true));var ndecisionStatus=inputParam.sample.ndecisionstatus;if(inputParam.sample.ndecisionstatus===undefined||inputParam.sample.ndecisionstatus===transactionStatus.DRAFT){ndecisionStatus=transactionStatus.PASS;}var inputData={ndecisionstatus:ndecisionStatus,userinfo:inputParam.userinfo,nprimarykey:inputParam.sample.npreregno,ncoareporttypecode:reportCOAType.SAMPLECERTIFICATEPRIVIEW,nreporttypecode:REPORTTYPE.SAMPLEREPORT,sprimarykeyname:"