ry",{ntransactionresultcode:selectedParam.ntransactionresultcode,userinfo:userInfo,fetchField:dataField}).then(function(response){if(response.data.length>0){masterData=_objectSpread(_objectSpread({},masterData),{},{enforceCommentsHistory:response.data});dispatch({type:DEFAULT_RETURN,payload:{loading:false,masterData:masterData,openChildModal:true,operation:"view",screenName:"IDS_ENFORCECOMMENTHISTORY",idsName:idsName,dataField:dataField}});}else{dispatch({type:DEFAULT_RETURN,payload:{loading:false,multilingualMsg:"IDS_NOPREVIOUSCOMMENTSFOUND"}});}}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function reportGenerate(inputParam){return function(dispatch){dispatch(initRequest(true));var inputData={npreregno:inputParam.sample.npreregno,userinfo:inputParam.userinfo,nregsubtypecode:inputParam.sample.nregsubtypecode,reportName:"RJ_Testwise_Report_QRCode",reportFormat:"pdf"};rsapi.post("reportjasper/generateCOAReport",inputData).then(function(response){if(response.data.rtn==="Success"){if(response.data.filePath){// let user = response;
var elnURL=response.data.filePath;window.open(elnURL,'_blank');}else{toast.info(intl.FormattedMessage({id:"IDS_ELNUIURLNOTAVAILABLE"}));}// document.getElementById("download_data").setAttribute("href", response.data.filePath);
// document.getElementById("download_data").click();
}else{toast.warn(response.data.rtn);}dispatch({type:DEFAULT_RETURN,payload:{loading:false,openChildModal:false,selectedRecord:{},loadEsign:false}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function getSubSampleChildTabDetail(inputData){return function(dispatch){if(inputData.ntransactionsamplecode.length>0){var inputParamData={ntransactionsamplecode:inputData.ntransactionsamplecode,userinfo:inputData.userinfo};var url=null;switch(inputData.activeSubSampleTab){case"IDS_SUBSAMPLEATTACHMENTS":url="attachment/getSubSampleAttachment";break;case"IDS_SUBSAMPLECOMMENTS":url="comments/getSubSampleComment";break;default:url=null;break;}if(url!==null){dispatch(initRequest(true));rsapi.post(url,inputParamData).then(function(response){var responseData=_objectSpread({},response.data);responseData=sortData(responseData);dispatch({type:DEFAULT_RETURN,payload:{masterData:_objectSpread(_objectSpread(_objectSpread({},inputData.masterData),responseData),{},{APSelectedSubSample:inputData.APSelectedSubSample// selectedTestCode: inputData.ntransactiontestcode,
}),loading:false,showFilter:false,activeTestTab:inputData.activeSubSampleTab,screenName:inputData.screenName}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});}else{dispatch({type:DEFAULT_RETURN,payload:{masterData:_objectSpread(_objectSpread({},inputData.masterData),{},{APSelectedSample:inputData.APSelectedSample}),loading:false,showFilter:false,activeSampleTab:inputData.activeSampleTab}});}}else{//toast.warn("Please Select a Sample");
toast.warn(intl.FormattedMessage({id:"IDS_PLSSELECTASAMPLE"}));}};}export function ViewPatientDetails(masterData,screenName,userInfo,viewdetails){return function(dispatch){dispatch(initRequest(true));var selectedRecord={ntestcode:viewdetails.test.ntestcode};rsapi.post("approval/getSampleViewDetails",{selectedRecord:selectedRecord,PatientId:viewdetails.test.spatientid,npreregno:viewdetails.test.npreregno,userinfo:userInfo}).then(function(response){masterData['AuditModifiedComments']=[];masterData['AuditModifiedComments']=response.data['AuditModifiedComments'];masterData['CurrentResult']=[];masterData['CurrentResult']=response.data['CurrentResult'];masterData['viewdetails']=[];masterData['viewdetails']=response.data['viewdetails'][0];//sortData(masterData);
dispatch({type:DEFAULT_RETURN,payload:{masterData:masterData,screenName:"IDS_PREVIOUSRESULTVIEW",operation:"",loading:false,openChildModal:true}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function getTestBasedCompletedBatch(inputData){return function(dispatch){dispatch(initRequest(true));rsapi.post("approval/getTestBasedOnCompletedBatch",inputData).then(function(response){dispatch({type:DEFAULT_RETURN,payload:{masterData:_objectSpread(_objectSpread({},inputData.masterData),response.data),loading:false}});}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function updateEnforceStatus(inputParam){return function(dispatch){dispatch(initRequest(true));rsapi.post("approval/updateEnforceStatus",inputParam.inputData).then(function(response){var responseData=_objectSpread({},response.data);responseData=sortData(responseData);replaceUpdatedObject(responseData["AP_SAMPLE"],inputParam.masterData.AP_SAMPLE,"npreregno");replaceUpdatedObject(responseData["AP_SUBSAMPLE"],inputParam.masterData.AP_SUBSAMPLE,"ntransactionsamplecode");replaceUpdatedObject(responseData["AP_TEST"],inputParam.masterData.AP_TEST,"ntransactiontestcode");delete responseData["AP_SAMPLE"];delete responseData["AP_SUBSAMPLE"];delete responseData["AP_TEST"];var masterData=_objectSpread(_objectSpread(_objectSpread({},inputParam.masterData),responseData),{},{APSelectedSample:replaceUpdatedObject(responseData.updatedSample,inputParam.masterData.APSelectedSample,"npreregno"),APSelectedSubSample:replaceUpdatedObject(responseData.updatedSubSample,inputParam.masterData.APSelectedSubSample,'ntransactionsamplecode'),APSelectedTest:replaceUpdatedObject(responseData.updatedTest,inputParam.masterData.APSelectedTest,'ntransactiontestcode')});var respObject={masterData:masterData,inputParam:inputParam,openChildModal:false,operation:"update",masterStatus:"",errorCode:undefined,loadEsign:false,showEsign:false,selectedRecord:{},loading:false};dispatch(postCRUDOrganiseTransSearch(inputParam.postParamList,respObject));}).catch(function(error){dispatch({type:DEFAULT_RETURN,payload:{loading:false}});if(error.response.status===500){toast.error(error.message);}else{toast.warn(error.response.data);}});};}export function checkReleaseRecord(inputParam){return function(dispatch){dispatch(initRequest(true));rsapi.post("approval/checkReleaseRecord",inputParam.inputData).then(function(response){var availableReleaseRecord;if(response.data.rtn=="Success"){availableReleaseRecord=true;}else{availableReleaseRecord=false;}dispatch({type:DEFAULT_RETURN,payload:{availableReleaseRecord:availableReleaseRecord,loading:false,action:inputParam.action,ncontrolCode:inputParam.inputData.performaction.ncontrolCode}});// else{
