   // result.Patient = [requiredValidator(formValueGetter('InstrumentCategory'))].filter(Boolean).reduce((current, acc) => current || acc, '');
    result.Instrument = [requiredValidator(_dataItem.InstrumentCategory)];
    return result;
  };
