                                            // data-for="tooltip-grid-wrap" 
                                             onClick={this.openDashBoard} className="zoom">
                    <FontAwesomeIcon icon={faChartPie} data-tip="Dashboard" ></FontAwesomeIcon>
                  </Nav.Link>
                  {/* </Tooltip> */}
                </DashboardIcon>
                {/* <Tooltip position="bottom" anchorElement="target" openDelay={100} parentTitle={true}> */}
               
